custom_css:
/*header*/
.navigation-wrapper{
    background:#f3c81c !important;
}
.btn.filled,
.page-numbers.current,
.articles-pagination a,
.page-numbers:hover,
.footer-product .add_to_wishlist,
.footer-product .add_to_wishlist:hover::after,
.footer-product .add_to_cart_button,
.footer-product .add_to_cart_button:hover::after,
.footer-product .show-quickly,
.footer-product .show-quickly:hover::after{
    border-radius:5px !important;
}
.footer-product .add_to_wishlist:hover,
.footer-product .add_to_cart_button:hover,
.footer-product .show-quickly:hover{
    background:#2b3743 !important;
}
#menu-currency li a,
#menu-language li a{
    color:#1c1a19;
}

.header-wrapper.header-advanced .header-search.act-default{
    margin: 0 !important;
    width: 100% !important;
}
.header-wrapper.header-advanced .header-search.act-default input[type="text"]{
  width:100%;
}
.menu-main-container .menu-item a:hover::after,
.menu-main-container .menu-item.current_page_item a::after{
    display:none;
}
#menu-secondary-menu li.menu-item a:hover{
    opacity:0.6;
}
#menu-main-menu-1 .menu-item:hover,
#menu-main-menu-1 .menu-item.current_page_item a{
    opacity:.6;
}
.pagination-cubic ul li a,
.articles-pagination a{
    border:1px solid #e3e3e3 !important;
    background:transparent;
}
.blog .articles-pagination{
    margin-bottom:6vw;
}
.search-form-wrapper .input-row{
    border:1px solid #e9e9e9;
    border-radius:5px;
}
.fixed-header{
    border-color:#fff !important;
}

 .navigation-wrapper:before,
 .btn.filled:before{
    display:none;
}
.badge-number{
    background:#4d88d1 !important;
}
.secondary-title{
    border-color:#f9d441 !important;
}
.secondary-title,
.secondary-title:after{
    color:#fff !important;
}
.secondary-menu-toggle{
    display:none !important;
}
.content-page{
    padding-bottom:0 !important;
}
#menu-secondary-menu{
    padding-top:16px;
    padding-bottom:16px;
    border-color:#f3c81c !important;
}
#menu-secondary-menu > li > a{
     padding-top:20px;
     padding-bottom:20px;
}

.top-bar .topbar-widget a{
    color:#8d8c8c;
} 

.navigation-wrapper .menu-wrapper.menu-align-center .menu {
    text-align: left !important;
}
.menu-inner .menu-wrapper .menu > li {
    padding: 0 3%;
    border-right:1px solid #f9d441;
}
.menu-inner {
    width: 100%;
    clear: both;
}
.secondary-menu-wrapper{
    width: 15%;  
    float: left;
}
.secondary-menu-wrapper + .menu-wrapper{
    width: 85%;
    float: left;
}


.page-numbers{
    font-size:12px;
    font-family:Lato;
    color:#a6a5a5;
    border-radius:5px !important;
}
.letter-spacing-1{
    letter-spacing:1px;
}

/*blog*/
.blog .blog-mask-inner a{
    padding-bottom:9px !important;
}

/*Bredcrumbs*/
.page-heading{
    margin:0 !important;
}
.template-container .page-heading .col-md-12 {
  display: -webkit-box;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: vertical;
  -webkit-box-direction: reverse;
  -webkit-flex-direction: column-reverse;
      -ms-flex-direction: column-reverse;
          flex-direction: column-reverse;
}


/*Shop*/
.widget_product_categories {
    border:2px solid #f3c81c;
}
.widget_product_categories .widget-title{
    font-size:16px !important;
}
.after-shop-loop{
    margin-bottom:6vw;
    padding:25px 0 20px 0 !important;
}
.price_slider_amount .button{
    background:#f3c81c;
   border-color:#f3c81c;
   border-radius:5px;
   font-size:13px;
   font-family: "Fredoka One";
   letter-spacing:1px;
}
.archive .product-hover-swap{
    border:1px solid #fff;
    margin-bottom:2px;
     transition:.4s;
}
.archive .product-hover-swap:hover{
    -webkit-box-shadow: 0px 0px 3px 0px #b6b5b5;
    -moz-box-shadow: 0px 0px 3px 0px #b6b5b5;
    box-shadow: 0px 0px 3px 0px #b6b5b5;
    transition:.4s;
}
input[type="text"]:focus, input[type="number"]:focus, input[type="email"]:focus, input[type="search"]:focus, input[type="password"]:focus, input[type="tel"]:focus, textarea:focus, textarea.form-control:focus {
    border-bottom:2px solid #f3c81c !important;
}
.single-product .carousel-area {
    margin-bottom: 5vw;
}
table.wishlist_table{
    margin-bottom:5vw;
}
.etheme_widget_brands .widget-title{
    padding:0px !important;
    color:#2b3746;
}
.widget_products .product_list_widget li{
    border-bottom:none;
}
.widget_product_categories.sidebar-widget ul li a,
.wpb_widgetised_column .widget_product_categories ul li a,
.sidebar-widget.etheme_widget_brands li a{
    font-weight:700;
}
.sidebar-widget.etheme_widget_brands li a span{
    color:#b1b1b1;
}
.sidebar-widget .product-categories {
    padding:25px !important;
}
.etheme_widget_brands .product-categories{
    padding-left:0 !important;
}
.widget_product_categories.sidebar-widget > ul > li:last-child > a{
    border-bottom:none;
}
.price_slider_wrapper .ui-slider .ui-slider-handle {
    width: 12px !important;
    height: 12px !important;
    top: -5px !important;
    -webkit-border-radius: 3px !important;
    border-radius: 3px !important;
}
.woocommerce-tabs li.active a,
.woocommerce-tabs li a{
    color:#231f20 !important;
   font-family:"Fredoka One";
   font-weight:500 !important;
   padding:15px 25px !important;
}
.single-product .menu-social-icons i,
.share-title{
    color:#b3b3b3 !important;
}
.single-product .menu-social-icons li:hover i{
    color:#f3c81c !important;
}
.single-product form.cart .add_to_cart_button {
    background:#f3c81c;
    border-color:#f3c81c;
    border-radius:5px;
    min-width:160px;
    font-family:"Fredoka One";
    font-weight:500;
    font-size:13px;
}
.onsale, .product-images .sale-value {
    background: #f3c81c;
    border-radius: 5px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    font-weight: bold;
    font-size: 16px;
    width: 50px;
    height: 50px;
    line-height: 50px;
}
.single-product a.add_to_wishlist:before{
    border-radius:5px;
}
.product-information-inner .woocommerce-Price-amount{
     font-family:"Fredoka One";
      font-weight:500;
      font-size: 36px;
      color:#f3c81c;
}
.woocommerce-tabs li.active span::after{
    bottom:-29px;
}
ins .woocommerce-Price-amount,
.woocommerce-Price-amount{
    color:#f3c81c !important;
}
.single-product .tabs-nav{
    border-bottom:1px solid #e6e6e6;
}
.badge-number,
.wishlist-count{
    background-color:#f3c81c !important;
}
del .woocommerce-Price-amount{
    color:#b6b6b6 !important;
}
.single-product .products-title{
    margin-bottom:40px;
}
.single-product .products-title span{
    font-size:40px;
}
.single-product .woocommerce-tabs {
    margin-bottom:0;
}
.single-product .tab-content {
    padding-bottom:0;
}
.my-acaunt-single input[type="text"], 
.my-acaunt-single input[type="email"]{
   border:1px solid #ebebeb;
   background:transparent;
   width:100%;
   height:44px;
   margin-bottom:20px;
}
.my-acaunt-single p{
    margin-bottom:10px;
}
.my-acaunt-single .left-block .wpb_wrapper{
    border:1px solid #ebebeb;
    padding:60px;
}
/*Contact form*/
.my-acaunt-submit input[type="submit"]{
    background: #2b3746;
    color: #fff;
    border-radius: 5px;
    width: 130px;
    height: 50px;
    font-family: Fredoka One;
    letter-spacing: 2px;
    margin-top:10px;
}
.my-acaunt-submit input[type="submit"]:hover{
    background:#37475b;
    border:none;
}
.container-form input[type="text"],
.container-form input[type="email"],
.container-form input[type="password"],
.container-form textarea{
    width:100%;
    border-top: none;
    border-right: none;
    border-left: none;
    border-color:#f9e38d;
    font-size:14px;
    background:transparent;
}
body .page-wrapper{
    background:transparent !important;
}

.container-form textarea{
    height:100px;
    margin-bottom:35px;
}
.container-form input[type="submit"]{
    font-size:13px;
    text-transform:uppercase;
    height:50px;
    font-family:"Fredoka One";
    font-weight:500;
    color:#fff;
    background:#f3c81c;
    border-radius:5px;
   
}
.responsive-center{
    margin-top:70px;
     text-align:center !important;
}

.mpc-button{
    cursor:pointer;
}
.page-id-4356 .content-page{
    padding-bottom:0 !important;
}




/*footer text*/
footer .wpcf7-validates-as-email{
    color:#969595;
}
.footer-text p{
    font-size:13px !important;
}
footer .mpc-divider-wrap{
    display:none;
}
footer .null-instagram-feed .instagram-pics li{
    padding:2px !important;
}

footer .et-mailchimp input[type="email"]:focus{
    border:1px solid #5c5c5c !important;
}
footer .footer-menu p a{
    color:#969595 !important;
}
.footer-copyrights a:hover i{
    color:#f3c81c;
}
.footer-bottom img:hover {
    opacity:1 !important;
}
.footer-copyrights a:hover strong{
    color:#f3c81c;
}
footer .footer-menu p a:hover{
    color:#f1f1f1 !important;
}

footer .et-mailchimp input[type="email"]::-webkit-input-placeholder{
    color:#a4a4a4;
}
footer .et-mailchimp input[type="email"]{
   background:transparent !important;
}
.et-mailchimp{
     margin-bottom:10px !important;
}
.footer-mailchimp-text{
    font-size:13px;
    margin-bottom:10px;
}
@media screen and (max-width: 1500px) {
  #menu-secondary-menu > li > a{
     padding-top:17px;
     padding-bottom:17px;
  }
}
@media screen and (min-width:992px){
    .secondary-menu-wrapper+.menu-wrapper{
        width: 78%;
    }
    .secondary-menu-wrapper{
        width: auto;
    }
}
@media screen and (max-width:768px) {
    .responsive_centr{
        text-align:center !important;
    }
}

