custom_css:
::selection{
    background:#cca966;
}
/*Header*/
.header-wrapper .header .container{
    padding-bottom:80px;
}
.header-wrapper .header  .container-wrapper,
.header-wrapper .top-bar{
    border-bottom:none;
}
.single-post.et-header-overlap .top-bar {
  background:#050709 !important;
}
.header-color-white .menu-wrapper .menu > li.current-menu-item > a,
.header-color-white .menu-wrapper .menu > li > a:hover{
    color:#cca966;
}
.menu-wrapper .menu > li > a{
    letter-spacing:4px;
}
.single-post .content-article.entry-content .vc_row-o-equal-height{
    margin-bottom:4vw !important;
}
.fixed-header > .container {
    padding-top: 1em;
    padding-bottom: 1em;
}
.instagram-pics li a img,
article img,
article .vc_row.wpb_row.vc_row-fluid{
  -webkit-filter: grayscale(100%);
  -moz-filter: grayscale(100%);
  -ms-filter: grayscale(100%);
  -o-filter: grayscale(100%);
  filter: grayscale(100%);
  filter: gray; /* IE 6-9 */
}
.block-team .vc_single_image-wrapper,
.mpc-grid__image.mpc-transition{
    overflow:hidden;
}
.block-team img,
.mpc-grid__image.mpc-transition img{
    transition: all 0.4s ease-in-out;
}
.block-team img:hover,
.mpc-grid__image.mpc-transition:hover img{
   box-shadow: 1px 1px 25px #f4f4f4;
    border-color: #f6f6f6 !important;
    transform: scale(1.06);
    transition: all 0.4s ease-in-out; 
}

.fixed-header{
    border-top:none;
}
.topbar-widget.etheme_widget_socials .et-follow-buttons a{
    border-left:none;
}
.mpc-button__title{
    letter-spacing:2px !important;
}
.top-bar .container p{
    font-size:12px;
    text-transform:uppercase;
    color:#fff;
}
.top-bar .container p span,
.top-bar .container .et-follow-buttons a{
    color:#bcbcbc !important;
}
.page-heading h1{
    letter-spacing:2px;
}
.letter-spacing-3{
    letter-spacing:3px;
}
.letter-spacing-2{
    letter-spacing:2px;
}
.letter-spacing-1{
    letter-spacing:1px;
}
body p{
    letter-spacing:1px !important;
}
/*Blog*/
article.blog-post .wp-picture.blog-hover-animated .btn, article.post-grid .wp-picture.blog-hover-animated .btn {
    top: -38px;
}
.home .post-heading h2 a{
    color:#cca966;
    font-size:24px !important;
    text-transform:initial !important;
}
article .content-article p{
    letter-spacing:1px;
    font-size:17px !important;
}
.home .meta-post time,
.home .meta-post .post-comments-count,
.home article .content-article p{
    color:#fff !important;
}
.post-heading h2{
    text-transform:initial !important;
}

.meta-post time:before,
.meta-post .post-comments-count:before,
body article .read-more{
    color:#cca966 !important;
}
body article span.read-more:before{
    background-color:#cca966 !important;
}
.blog article {
    text-align:center;
    
}
.single-post article{
    overflow:hidden;
}
.post-heading {
    margin-bottom: 20px;
}

.read-more{
    margin:0 auto;
    font-family:Arvo;
    font-weight:700;
    font-size:16px;
    color:#1c1a1a !important;
}
.read-more:before{
    background-color:#1c1a1a !important;
}
.null-instagram-feed li a .insta-info span {
    padding: 0 0.8em;
}

/*Footer*/
footer.footer .menu-main-menu-container li a,
footer.footer .menu-right-menu-container li a{
    font-family: Arvo;
    text-transform: capitalize;
    line-height: 16px;
    font-weight: 700;
    font-style: normal;
    color: #fff;
    font-size: 16px;
}
.footer {
    padding: 4em 0 1.86em;
}

.footer .menu {
    margin-bottom: 30px;
}
footer.footer ul li a{
    letter-spacing:3px;
    color:#fff !important;
} 
footer.footer ul li a:hover{
    color:#cca966 !important;
} 
.page-heading h1.title span{
    display: inline-block;
    vertical-align: middle;
    font-style: normal;

}
article p{
    line-height:27px !important;
}
.comment-reply-title{
    padding: 0.7em 0 0 0 !important;
    border:none !important;
    
}
.comments .title-alt{
    text-transform:initial !important;
     padding:0;
    border:none;
    margin-bottom: 2em;
}
.comments .title-alt,
 #reply-title{
     font-size:45px !important;
 }
.page-heading h1.title span:after,
.head-img:after,
.comments .title-alt:after,
 #reply-title:after{
    content: url(http://8theme.com/demo/xstore/barbershop/wp-content/uploads/sites/46/2017/08/heading-img.png);
    margin-left: 20px;
    position:relative;
    bottom:12px;
    }

@media screen and (min-width: 1900px) {
 
  .header-bg-block{

    background-size: cover;
    background-image: url(http://8theme.com/demo/xstore/barbershop/wp-content/uploads/sites/46/2017/08/header-bg.png) !important;
    background-position: 0 244px;
  }
}


  
  .blog .post-heading h2 a{
      font-size:24px;
     
  }
   .blog .post-heading h2{
        margin-bottom:20px !important;
   }
  .meta-post{
      font-size:17px !important;
      color:#aeaeae;
  }
  .content-article p{
      font-size:17px;
      color:#aeaeae;
  }


.links,
.header-color-white .menu-wrapper .menu > li.current-menu-item > a:after,
.header-color-white .menu-wrapper .menu > li > a:hover:after,
.post-categories{
    display:none !important;
}
body.home .header-bg-block, .header-vertical, .header-vertical2 {
    background:transparent !important;
}

@media screen and (max-width: 480px) {
 
 .wpcf7 .wpcf7-form p{
     text-align:center !important;
 }
 .mpc-tabs ul li.mpc-tabs__nav-item{
     margin-right:10px !important;
 }
 .albums-tabs ul {
    width: 100% !important;
}
.fixed-header .header-logo img{
    max-height:60px !important;
}
.section_tree .wpb_column.vc_column_container{
    margin-bottom:50px;
}
.fixed-header .container {
    padding-top: 0.6em;
    padding-bottom: 0.6em;
}
.see-all-button{
    display:none;
}
.slider-with-space p > img{
    display:none;
}
body .responsive-section{
    padding-top:160px !important;
    padding-bottom:160px !important;
}
 body .page-heading {
     padding-top:180px !important;
     padding-bottom:0 !important;
     
 }
 body .section_tree .wpb_column .vc_column-inner {
     margin:0 !important;
 }
 .page-heading h1{
     font-size:34px !important;
     
 }
 
 .block-team{
     margin-bottom:80px;
 }
 .first-section .wpb_single_image{
     margin-left:0 !important;
 }
 .responsive-center *{
     text-align:center !important;
 }
.first-section > .wpb_column:last-child > .vc_column-inner{
    padding-right:0px !important;
}
body .first-section{
    margin-bottom:0 !important;
}
.footer {
    padding: 3em 0 1.86em;
    margin-top:-3px !important;
}
}
