custom_css:
::selection {background: #c0a37b;}
/*Top Bar*/
.top-bar #menu-languages-menu li::after,
.top-bar #menu-currency-menu li::after{
    color:#d2d2d0;
}

.top-bar{
    max-width:1300px;
    margin:0 auto;
}
.post-comments-count:before{
    color:#be9a67 !important;
}
body .fixed-header .shopping-container.ico-design-4 .badge-number {
    top: -7px !important;
}
/*blog*/
body .entry-date::before{
    color:#be9a67;
}
body .post-comments-count{
    color:#a4a4a4 !important;
}
body #etheme-popup {
    width: 800px !important;
    height: 372px !important;
    background-color: #fff !important;
    padding:0;
}
#etheme-popup .et-mailchimp input[type=email]{
    background:#f5f5f5;
    color:#9b9999;
    height:44px !important;
}
.mc4wp-form .et-mailchimp input[type="submit"]{
        background-size: 20px !important;
}
.single-product .tab-title:before {
    content: "";
    width: 4px;
    height: 4px;
    background: #dddddd;
    display: block;
    position: absolute;
    right: 0;
    transform: translateY(-50%);
    top: 50%;
    border-radius: 100%;
}
body .wc-tabs li:last-child a::before{
    display:none !important;
}
.blog .read-more{
    color:#1c1a19;
    text-align:center !important;
    margin:25px auto 0 auto !important;
}
.read-more:hover{
    color:#be9a67;
}
/*.blog .read-more::before{*/
/*    background-color:#1c1a19 !important;*/
/*}*/
.fixed-header{
    border:none !important;
}
.tabs-nav li a{
    padding:5px 20px !important;
}
body .product-information-inner h1{
    font-size:30px !important;
}
.blog .post-heading-inner .entry-title{
    font-weight:700;
}
.blog .post-heading-inner .entry-title{
    font-size: 30px;
    color: #1c1a19;
    letter-spacing:2px;
    margin-bottom:25px;
    font-family:Cormorant Garamond;
}
.hover-btn:hover{
    background:#bf9b69;
    cursor:pointer;
}

.hover-btn-2:hover{
    background:transparent !important;
    cursor:pointer;
    border:2px solid #be9a67;
}
.testimonial-block .mpc-testimonial{
-webkit-box-shadow: 0px 0px 40px 0px rgba(242,242,242,1);
-moz-box-shadow: 0px 0px 40px 0px rgba(242,242,242,1);
box-shadow: 0px 0px 40px 0px rgba(242,242,242,1);
}
 .post-categories{
    display:none;
}
.product-details .product-title a{
    font-family:Cormorant Garamond;
    font-size:17px;
    font-weight:700;
    color:#1c1a19;
}
body .tabs-block .mpc-tabs.mpc-transition{
     background-position: center 243px !important;
}
.slider-container .blog-hover-animated,
.slider-container .grid-post-body{
    width:100% !important;
}
.slider-container .post-heading h2 a{
    font-size:20px !important;
    font-family:Cormorant Garamond;
    font-weight:700;
}
body .woocommerce-Price-amount{
    font-family:Lora !important;
    color:#1c1a19;
    font-weight:500;
}
body .slider-container article.blog-post .read-more,
body .slider-container article.post-grid .read-more {
    display: block !important;
    margin-top: 10px;
    margin-bottom: 10px;
     display: table !important;
     color:#1c1a19;
}
body .slider-container article.blog-post .read-more:hover,
body .slider-container article.post-grid .read-more:hover{
    color:#be9a67;
}

.articles-pagination{
    margin-bottom:6vw;
    border:none;
}
.articles-pagination .current,
.articles-pagination a:hover{
    border-radius:50%;
    color:#fff !important;
}
.articles-pagination a{
    color:#666666 !important;
    border:none;
}

.post-heading{
    margin-bottom:15px;
}
.post-heading h2{
    margin-bottom:20px;
}
.block-linia img{
    margin-top:-25%;
}

/*Shop*/
form.login input,
form.register input{
    border:1px solid #e6e6e6 !important;
}

.product-information .woocommerce-product-rating{
    margin-bottom:0.7em !important;
}
.product-information .products-page-cats{
    margin: -0.65em 0 0.6em !important;
}
.product-information-inner .add_to_cart_button{
    font-family:Cormorant Garamond;
    font-weight:700;
    font-size:14px;
}
.products-per-page span{
    color:#1c1a19 !important;
}
.price_slider_wrapper .button,
.open-filters-btn a{
    background-color: #be9a67;
    border-color: #be9a67;
    border-radius:50px;
    color:#fff !important;
}
.content-product .product-image-wrapper {
    padding: 15px;
}

.content-product.product-inview .product-image-wrapper:hover .product-content-image{
    background:#000;
}
.content-product.product-inview .product-image-wrapper:hover img {opacity: 0.5;}

.open-filters-btn a:hover,
.open-filters-btn a.active{
    background:#1c1a19 !important;
    border-color:#1c1a19 !important;
    color:#fff !important;
}
.price_slider_wrapper .price_slider_amount span {
    color: #be9a67 !important;
    font-weight: bold !important;
}
.price_slider_amount .button{
    font-size:13px;
}
.price_slider_amount .button:hover{
    background:#1c1a19 !important;
    opacity: 0.8 !important;
}
.price_label{
    color:#666666;
}
.author-page .owl-images-carousel .image-item img {
    opacity:1;
}
.author-page .wpb_images_carousel .owl-controls {
    margin-top: -50px;
    position: relative;
}
.author-page .owl-carousel .owl-pagination .owl-page {
    border: 2px solid #be9a67;
     background: transparent; 
    margin: 0 0.2em;
}
.post-comments-count{
    color:#be9a67 !important;
}
.switch-grid a,
.switch-list a{
    border-radius:50px;
  
}

.switch-grid a:hover::before,
.switch-list a:hover::before,
.switch-grid.switcher-active a::before,
.switch-list.switcher-active a::before{
    color:#fff !important;
}
.switch-grid a::before,
.switch-list a::before{
    color:#b5b5b5 !important;
}
.pagination-cubic ul li span.current{
    border-radius:100px;
}
del .woocommerce-Price-amount,
.page .price .woocommerce-Price-amount{
    color:#1c1a19;
    font-size:16px;
    font-weight:500;
}

.header-color-dark .et-wishlist-widget .wishlist-count,
.header-color-dark .shopping-container.ico-design-4 .cart-bag .badge-number{
    background:#be9a67 !important;
}
.woocommerce-billing-fields input{
    border:1px solid #e6e6e6 !important;
}
.shopping-container .shopping-cart-widget .woocommerce-Price-amount,
.yith-wcwl-add-button a{
    color:#be9a67 !important;
}
.product-information .price del .amount, .product-info-wrapper .price del .amount, .product-summary-center .price del .amount {
    font-size: 28px;
}
.woocommerce-tabs .wc-tabs span::after{
    display:none;
}
.single-product .products-title span{
    font-size:40px;
}
.pagination-cubic .page-numbers a,
.pagination-cubic .page-numbers span{
    font-family: Lora;
    padding: 0 14px;
    font-size: 15px;
    font-weight: 400;
    border-radius:50px;
}
.single-product .carousel-area,
.shop_table.cart.wishlist_table{
    margin-bottom: 6vw;
}
.product-add-to-cart a{
    border-radius:100px !important;
}
.bag-total-table{
    margin-bottom: 5vw;
}

body .tab-description .vc_row.wpb_row{
    margin-bottom:0 !important;
}
.pagination-cubic .page-numbers a,
.woocommerce-result-count{
    color:#666666;
}
.single-product .menu-social-icons a i,
.product-share .share-title{
    color:#1c1a19 !important;
}
.single-product .menu-social-icons a:hover i{
    color:#be9a67 !important;
}
.single-product .add_to_cart_button,
.woocommerce-message .button, .woocommerce-error .button, .woocommerce-info .button {
    border:2px solid #be9a67;
    border-radius:50px;
    background:transparent;
    color:#1c1a19;
}
.single-product .add_to_cart_button:hover, .woocommerce-message .button:hover, .woocommerce-error .button:hover, .woocommerce-info .button:hover{
    color:#fff;
}


/*content*/
.after-shop-loop{
    margin-bottom:6vw !important;
}
.testimonial-block .mpc-testimonial{
    width:80%;
    margin:20px auto;
}
.back-history:hover {
    color:#1c1a19 !important;
}
.onsale{
    background:#be9a67;
}
.carousel-area .owl-prev:hover,
.carousel-area .owl-next:hover{
    background:#fff !important;
}
.carousel-area .owl-prev:hover::before,
.carousel-area .owl-next:hover::before{
    color:#be9a67;
}
.carousel-area .owl-prev,
.carousel-area .owl-next{
    color:#62615f;
}
body .page-wrapper{
    background:transparent !important;
}

article.blog-post .wp-picture.blog-hover-animated .btn, article.post-grid .wp-picture.blog-hover-animated .btn {
    top: -41px !important;
}
.page-heading.bc-type-left2.bc-color-white .breadcrumbs a {
    color: #1c1a19;
    letter-spacing: 1px;
}
.letter-spacing2{
    letter-spacing:2px;
}
.content-page{
    padding-bottom:0 !important;
}

.mc4wp-form .et-mailchimp input[type="email"]::-webkit-input-placeholder{
    color: #9f9f9f !important;
} 
.mc4wp-form .et-mailchimp input[type="email"]:focus {
   border-bottom:none !important;
}
.slide-view-timeline2 .meta-post-timeline .time-day {
    margin-bottom: -5px !important;
    margin-top: 10px !important;
}


body .subscribe-block input[type="email"]:focus{
    border:2px solid #fff !important;
}
.subscribe-block .wpcf7-email{
    margin-bottom:0 !important;
    height:54px;
    background:transparent !important;
    border:2px solid #d8c2a4 !important;
    float:left;
    width:95% !important;
     border-radius: 40px 0 0 40px !important;
     margin-right:-2px;
     color:#fff;
     padding:0 25px;
}
.subscribe-block .wpcf7-submit{
    margin-top:0 !important;
    border-radius: 0 40px 40px 0 !important;
    background:#fff !important;
    color:#1c1a19 !important;
    position: absolute;
    right: 15px;
     height:54px;
     line-height:26px !important;
     font-family:Cormorant Garamond;
     font-size:14px;
     padding:0 25px;
     font-weight:700;
}

 input[type="email"]::-webkit-input-placeholder{
    color: #d8c2a4 !important;
} 

.subscribe-block .wpcf7-submit:hover{
    border-color:#fff !important;
    color:#be9a67 !important;
}
.subscribe-block .ajax-loader{
    display:none;
}
input[type="text"]:focus, input[type="number"]:focus, input[type="email"]:focus, input[type="search"]:focus, input[type="password"]:focus, input[type="tel"]:focus, textarea:focus, textarea.form-control:focus {
    border-bottom:2px solid #be9a67 !important;
}
/*Footer*/
footer.footer .et-follow-buttons a{
    font-size:30px;
    margin:0 14px;
}

.post-data:hover{
    border:4px solid #be9a67;
}
@media only screen and (min-width:1200px){
   .blog .content article:nth-child(odd) .post-data{
    text-align:center;
    padding:64px 85px;
    background:#fff;
    position:relative;
    left:-70px;
     z-index:99;
     display: inline-table;
    width: 610px;
}
.blog .content article:nth-child(even) .post-data{
    text-align:center;
    padding:64px 85px;
    background:#fff;
    position:relative;
    right:-70px !important;
    z-index:99;
    display: inline-table;
    width: 610px;
}
}
@media only screen and (max-width:1200px){
  body .tabs-block .mpc-tabs.mpc-transition{
     background-position: center 218px !important;
}
}

@media only screen and (max-width:991px){
    .subscribe-block .wpcf7-submit{
        padding:15px !important;
    }
     .subscribe-block .wpcf7-email{
         width:91% !important;
     }
    .block-linia img{
      margin-top:-34%;
    }
    .testimonial-block .mpc-carousel__wrapper .mpc-testimonial{
         padding:15px !important;
         margin: 20px auto 85px auto;
     }
     .block-new-book{
         display:none;
     }
     .responsive-width{
         width:100%;
         text-align:center;
     }
     .block-booking h2{
         text-align:center !important;
     }
}
@media only screen and (max-width:768px){
  
      .mpc-tabs__content{
      background:#eaeaea;
      margin-bottom:90px;
  }
    .mpc-tabs__content .mpc-tab.mpc-container{
       padding-top:0 !important;
  }
    footer.footer,
    body  .subscribe-block {
     padding-top: 40px !important;
     padding-bottom: 40px !important;
    }
    .responsiv-block-padding{
        padding-left:15px !important;
        padding-right:15px !important;
    }
    .heading-padding-top_responsive{
        padding-top:30px;
    }
    .block-linia img{
        display:none;
    }
    .subscribe-block .wpcf7-submit,
    .subscribe-block .wpcf7-email{
        margin-top:30px !important;
        margin-bottom:10px !important;
     }
       body .tabs-block .mpc-tabs.mpc-transition{
         background-position:transparent;
      }
    .post-data{
        text-align:center !important;
        padding:15px 0;
        background:#fff;
     }
    body .section-map-heading .text-center .vc_column-inner{
         position:relative;
         bottom:-35px;
         padding:40px 15px 100px 15px !important;
     }
     
    
   
}
@media only screen and (max-width:480px){
    body #etheme-popup{
        height:auto;
    }
    body .popap-block .vc_column-inner{
    padding-top: 18px !important;
    padding-right: 20px !important;
    padding-bottom: 0 !important;
    padding-left: 20px !important;
   }
  .fixed-header .et-wishlist-widget i {
    font-size: 1.5em !important;
   }
   .content-product .product-image-wrapper{
       padding:0 !important;
   }
  
  .after-shop-loop,
  .articles-pagination,
  body .last-row{
      margin-bottom:80px !important;
  }
  body .responsive-padding-bottom{
      margin-bottom:50px !important;
  }
  .margin-top-resp{
      margin-top:60px;
  }
   .mpc-tabs__nav ul .mpc-tabs__nav-item{
        margin:0 !important;
    }
     .mpc-tabs__nav ul .mpc-tabs__nav-item .mpc-button__title{
        padding-left:10px !important;
        padding-right:10px !important;
    }
    .mpc-tabs__nav ul .mpc-tabs__nav-item, .mpc-tabs__nav ul .mpc-tabs__nav-item:last-child {
    margin-bottom: 0em !important;
}
.single-post p.form-submit{
    margin-bottom:80px !important;
}
}
@media only screen and (min-width: 992px){
    .product-content .row {
     display: -webkit-box; 
  display: -moz-box;
  display: -ms-flexbox;
  display: -webkit-flex; 
  display: flex;
  -webkit-box-align: center;
  -moz-box-align: center;
  -ms-flex-align: center;
  -webkit-align-items: center;
  align-items: center;
    }
}
