custom_css:
::selection {background: #0b58a4;}
/*header*/
.header.main-header.header-bg-block {
   margin-bottom: 4vw;
    padding-bottom: 3vw;
}
.navbar-header{
    display:none;
}
#menu-main-menu-1{
    text-align:right !important;
}
.fixed-header{
    border:none !important;
}
.dropcap.light{
    color:#0b58a4 !important;
}
.video-icon i{
    width: 100px;
    height: 100px;
    line-height: 100px;
    padding:0 !important;
    box-shadow: 0 10px 15px rgba(0,0,0,0.4);
}
.video-icon i:before{position:relative;left:4px;}
.post-comments-count:before,
.posts-slider article .meta-post .entry-date:before{
    color:#0b58a4 !important;
}
.home-video .vc_column-inner{
    margin-right:-100px;
}
body .first-section-left.video-popup > .vc_column-inner{
    min-height:450px !important;
}
body .current-menu-item.current_page_item a,
.menu-wrapper .menu > li > a:hover{
    color:#0b58a4 !important;
}
.recent-posts-widget .post-widget-item h4 a{
  font-family:Poppins !important;   
}
.cat-item{
    color:#0b58a4;
}

div.first-section {
    background-position: bottom !important;
    background-size: cover !important;
}
.menu-wrapper .menu > li > a {
       padding: 0.47em 1.7em;
}
.menu-wrapper .menu > li > a:hover::after,
.menu-wrapper .menu > li > a::after{
   display:none;
}
.roboto{
    font-size: 40px;
    color: #1c1a1a;
    line-height: 50px;
    text-align: center;
    font-family: Roboto;
    font-weight: 400;
    font-style: normal;
}
.playfair-italic{
    font-size: 40px;
    color: #0b58a4;
    line-height: 50px;
    text-align: center;
    font-family: Playfair Display;
    font-weight: 400;
    font-style: italic;
}
body #menu-main-menu-1 li:last-child a:hover{
    border:2px solid #1c1a1a;
    color:#fff !important;
    background:#1c1a1a;
}
body #menu-main-menu-1 li:last-child a{
    border:2px solid #0b58a4;
    font-weight:900 !important;
    letter-spacing:3px;
    color:#fff !important;
    background:#0b58a4;
    font-size:11px;
   padding:8px 25px;
   border-radius:25px;
   margin-left:20px;
   margin-right:20px;
}
/*blog*/
.post-categories{
    display:none;
}
.blog .meta-post a{
    color:#0b58a4;
}
.post-heading h2{
     font-weight: 400 !important;
     text-transform:capitalize;
}
.post-heading h2 a{
    font-family:Roboto  !important;
    font-weight: 400 !important;
    font-size: 24px !important;
    color: #1c1a1a;
    letter-spacing:0 !important;
}
.widget-title{
    font-size: 20px;
    text-transform: none;
}
article.blog-post .wp-picture.blog-hover-animated .btn{
    top: -40px;
}



/*footer*/
.footer-menu-wrapper {
    margin-bottom:0;
    display:inline-table;
}
.footer-menu-wrapper li{
    float:left;
}
.footer-menu-wrapper li a{
    display:block;
    padding:8px 35px;
    font-family:Roboto;
    font-weight:700;
    color:#151717 !important;
    text-transform:uppercase;
    font-size:13px;
}
.footer-menu-wrapper li a:hover{
    color:#0b58a4 !important;
}
.letter-3{
    letter-spacing:3px;
}
.letter-1{
    letter-spacing:1px;
}
.text-copiright{
    clear:both;
    font-family: Poppins !important;
}
.text-copiright .wpb_wrapper{
    color:#777777;
    font-size:15px;
  font-family: Poppins;
   
}
body footer.footer{
    background-position: bottom;
    background-repeat: repeat-x !important;
    padding:180px 0 20px !important;
}

.articles-pagination a {
  border:none;
}
.articles-pagination {
  
    border-bottom:none;
   
}
 .widget-title span{
    letter-spacing:0 !important;
}
@media screen and (min-width: 1300px) {
   footer.footer .container{
    width:1400px !important;
    margin:0 auto !important;
   }
}
@media screen and (max-width: 980px) {
  body .section-bg-repeat{
       background-size:cover !important;
       padding-top: 160px !important;
    padding-bottom: 160px !important;
   }
   body footer.footer{
        background-size:cover !important;
        padding: 120px 0 20px !important;
   }
}

@media screen and (max-width: 480px) {
    div.first-section .mpc-button{
    margin-bottom:50px;
}
.fixed-header .container{
    padding-top:10px !important;
    padding-bottom:10px !important;
}
 footer.footer *{
     text-align:center !important;
 }
 body .first-section{
     padding:160px 0 !important;
     margin-bottom:100px !important;
 }
 .footer-menu-wrapper li a{
    display:block;
    padding:8px 7px;
    margin-bottom:15px;
 }
 footer.footer .wpb_single_image{
     margin-bottom:30px !important;
 }
 footer.footer .et-follow-buttons {
     margin-bottom:15px;
 }
 .first-section-left,
 .first-section-right > .vc_column-inner{
     padding-left:15px !important;
 }
 .responsive-text-center *{
     text-align:center !important;
 }
 .icon-section .vc_column-inner{
     padding-right:15px !important;
 }
 .section-three-img{
     padding-left:15px !important;
 }
 .responsiv-margin-bottom{
     margin-bottom:30px;
 }
 .section-bg-repeat .vc_row.wpb_row,
 .testimonial-section .wpb_column > .vc_column-inner,
 .price-section .vc_row.wpb_row{
margin-left:0 !important;
margin-right:0 !important;
margin-bottom:20px;
 }
 .margin-left-none .vc_column-inner,
 .center-price .vc_column-inner{
     margin-left:0 !important;
 }
 .play-button {
    position: relative;
    top: -92px !important;
}

.team_block{
    margin-bottom:60px;
}
body .section-video{
    margin-bottom:0 !important;
}
form p{
    text-align:center !important;
}
body .section-bg-repeat {
    padding-top: 110px !important;
    padding-bottom: 30px !important;
}
}
