custom_css:
.page-wrapper .vc_custom_heading {
    text-transform: none;
}

.content-page, .page-content {
    padding-bottom: 0em; 
}

.shopping-container .cart-popup-container,
.navbar-header .et-wishlist-widget .wishlist-dropdown {
    background-color: #fdf8e9;
}
.button, .btn, input[type="submit"],
.carousel-area .owl-next,
.carousel-area .owl-prev {
    border: 1px solid #f5f1e6;
    background-color: #f5f1e6;
}

.fixed-header {
    -webkit-box-shadow: 1px 1px 1px rgba(102,41,41,0.5);
    box-shadow: 1px 1px 1px rgba(102,41,41,0.5);
}
table thead th,
.woocommerce-wishlist .woocommerce table.wishlist_table thead th,
.cart_totals > h2,
.woocommerce-checkout .step-title{
    border-bottom: 2px solid #662929;
}
.woocommerce table.wishlist_table thead th, .woocommerce table.wishlist_table tbody td,
table, table th, table td, .cart-popup-container .product_list_widget > p,
.product_list_widget li,
.block-title,
.cart-popup-container,
.cart_totals th,
.step-title{
    border-color: rgba(102,41,41,0.1);
}
.cart_totals,
.quantity.buttons_added span,
.woocommerce table.wishlist_table .product-remove a,
.woocommerce table.wishlist_table td.product-add-to-cart a{
    background-color: #f5f1e6;
}
/*--- Shop page ---*/
.products-grid .content-product {
    background-color: #fff;
    padding: 15px;
}
.content-product .product-title a {
    font-size: 20px;
}
.content-product .products-page-cats a {
    font-size: 10px;}
.content-product .price {
    color: #ed0101;
    font-size: 15px;
    font-weight: normal;
}

.price ins {font-weight: normal;}
.product-images .sale-wrapper.with-percentage .sale-value{
	left: 75% !important;
}
.content-product .sale-wrapper.with-percentage .sale-value,
.product-images .sale-wrapper.with-percentage .sale-value{
    border-radius: 0;
    top: 0;
    left: inherit;
    width: 70px;
    height: 55px;
    font-size: 27px;
    line-height: 70px;
    background: #ed0101;
}
.products-grid .content-product .sale-wrapper.with-percentage .sale-value,
.products-grid .product-images .sale-wrapper.with-percentage .sale-value{
    right: 15%;
}

.content-product .sale-wrapper.with-percentage .sale-value:after,
.product-images .sale-wrapper.with-percentage .sale-value:after{
    content: '';
    position: absolute;
    right: 0;
    top: 55px;
    border: 18px solid transparent;
    border-top: 10px solid #ed0101;
    border-right: 30px solid #ed0101;
}
.content-product .sale-wrapper.with-percentage .sale-value:before,
.product-images .sale-wrapper.with-percentage .sale-value:before{
    content: '';
    position: absolute;
    left: 0;
    top: 55px;
    border: 18px solid transparent;
    border-top: 10px solid #ed0101;
    border-left: 30px solid #ed0101;
}

.product-view-default .footer-product .yith-wcwl-add-to-wishlist {
    left: -10px;
}
.quick-view-popup.mfp-with-anim {
    background-color: #fff;
}
.filter-wrap,
.after-shop-loop {
    padding: 1.43em 1.43em 0;
    border-top: none;
    border-bottom: none;
    background-color: #fff;
}
.after-shop-loop {
    padding-bottom: 1.43em;
}
.single-product .product-information-inner {
    padding: 14%;
    background-color: #fff;
}
.single_add_to_cart_button {
    background-color: #ed0101;
    border-color: #ed0101;
}
.single_add_to_cart_button:hover {
    border-color: rgba(237, 1, 1, 0.7) !important;
    background-color: rgba(237, 1, 1, 0.7) !important;
}
.product-information .share-title, 
.product-info-wrapper .share-title, 
.product-summary-center .share-title,
.qty-span,
.compare.button,
.product-information .product_meta,
.product-content .compare:before{
    color: #662929;
}
.group_table td.price {
    width: 21%;
}

/*--- Blog ---*/
.content-small .post-heading {
    padding-top: 3%;
}
.related-posts .title {
    font-size: 22px;
}
.single-post .no-comments {
    display: none;
}
.share-post {
    border-top: 1px solid rgba(102,41,41,0.1);
}
.mark-text.paragraph-boxed{
    background-color: rgba(102,41,41,0.1);
}
/*--- Footer ---*/
.footer .footer-widget {
    margin-bottom: 0;
}
@media only screen and (min-width: 992px) {
.header-wrapper.header-two-rows .header-top .container-top-wrapper {
    padding: 60px 0 35px 0;
}
.header-wrapper.header-two-rows.header-color-white .container-wrapper {
    border-top: transparent;
    border-bottom: transparent;
}
.header-wrapper.header-two-rows .menu-wrapper .menu > li {
    margin: 0 5px;
}
.header-wrapper.header-two-rows .topbar-color-white .languages-area .menu > li > a {
    font-weight: 700;
}
}
@media only screen and (max-width: 992px){
.header-wrapper.header-two-rows .container-top-wrapper {
    border-bottom: transparent;
}
}

@media only screen and (max-width: 480px){
.page-heading {
    padding-top: 11em !important;
}
.products-grid .product {
    width: 100%;
}}
