custom_css:
::selection {background: #f99d58;}
/*Header*/
.home .menu-wrapper .menu > li > a{
    color:#fff;
}
.header-wrapper.header-double-menu .container-wrapper {
    border:none;
}
body .template-content .fixed-header{
    border-top:none;
    border-bottom:2px solid #f9a058 !important;
}
ul li.menu-item a:after{
    background:#f9a058 !important;
} 
.page-heading {
    margin-bottom: 3.6em;
}
.section-home-coctails .mpc-wc-product a {
    pointer-events: none;
}
.single-post .form-submit input[type='submit']{
    border:none;
    border-radius:25px;
    background: linear-gradient(38deg, rgba(249,90,89,1) 0%, rgba(250,200,86,1) 73%, rgba(250,200,86,1) 100%); /* w3c */

}
.etheme_portfolio-template-default .prefooter{
    display:none;
}
article.blog-post, article.post-grid {
     margin-bottom: 0px; 
}
.single-post .form-submit input[type='submit']:hover{
 background: linear-gradient(to right, rgba(248,89,89,1) 0%, rgba(248,89,89,1) 100%);

}
/*Post*/
body.blog .blog-masonry{
    margin-bottom:5vw;
}
.container.content-page{
    margin-top:0 !important;
}
 .post-categories{
    display:none;
}
.meta-post.et-shortcode{
    text-align:center;
}
body.home .fixed-header li a{
    color:#5a5555 !important;
}
body .banner:hover .banner-content {
    top:32% !important;
}


.post-heading h2 a{
    text-align:center !important;
    font-size:24px;
    text-transform:none !important;
    line-height:32px;
        font-family: Josefin Slab !important;
        font-weight:700 !important;
}
body .post-heading h2{
    margin-bottom:25px !important;
}
 .post-heading {
    margin-bottom: 1.5em;
}
body.blog article.post-grid,
body.home article{
    text-align:center !important;
}
body .content-article .read-more:hover {
    opacity:1 !important;
     background:url(http://8theme.com/demo/xstore/coctails/wp-content/uploads/sites/44/2016/05/banner-button-hover.png) no-repeat;
}
article.blog-post .wp-picture.blog-hover-animated .btn, article.post-grid .wp-picture.blog-hover-animated .btn{
    top:-38px;
}
.articles-pagination{
    display:none;
}
.post-comments-count::before{
    color:#f9a058;
}
.posts-slider article .meta-post .post-comments-count {
    float: inherit;
    margin-left: 35px;
}
.meta-post,
 .meta-post time,
.meta-post span,
.meta-post a{
    color:#f9a058;
    font-family: "Finger Paint";
    font-size:16px !important;
}
 body .content-article .read-more {
    font-family: Josefin Slab;
    font-weight:700;
    border:none;
    margin:40px auto;
   padding: .8em 7em !important;
    letter-spacing: 1px !important;
    background:url(http://8theme.com/demo/xstore/coctails/wp-content/uploads/sites/44/2016/05/banner-button-1.png) no-repeat;
}
.post-heading h2 {
    margin-bottom: 0.79em;
}
body .vc_row.wpb_row.vc_row-fluid.visible{
    overflow:visible !important;
}
.plus-img,
.plus-img-2{
    position:relative;
}
.plus-img:before{
    content: url(http://8theme.com/demo/xstore/coctails/wp-content/uploads/sites/44/2017/08/plus.png);
    position:absolute;
    top:160px;
    right:-10px;
}
.plus-img-2:before{
    content: url(http://8theme.com/demo/xstore/coctails/wp-content/uploads/sites/44/2017/08/plus.png);
    position:absolute;
    top:160px;
   right:-10px;
}

/*Footer*/

footer.footer .widget-title{
    display:none;
}
footer.footer .sidebar-widget.null-instagram-feed .instagram-pics li{
    padding: 0px 4px 4px 0;
}
footer.footer .et-mailchimpm input[type="email"]{
   font-family: Yantramanav;
    margin-bottom:0px !important;
    height:40px;
    background:transparent !important;
    border:1px solid #d1d1d1 !important;
    float:left;
    width:95% !important;
     margin-right:-2px;
     color:#464646;
     padding:0 25px;
     font-size:13px;
     font-weight:400;
}

footer.footer .et-mailchimpm input[type="submit"]:hover{
    background:url(http://8theme.com/demo/xstore/coctails/wp-content/uploads/sites/44/2017/07/subscribe-bg-hover.png) no-repeat;
    border:none;
    
}
footer.footer .et-mailchimpm input[type="submit"]{
    transform:none !important;
    opacity:1;
    border:none;
    margin-top:0 !important;
    color:#fff !important;
    position: absolute;
    right: 15px;
     height:40px;
     line-height:26px !important;
     font-size:12px;
     padding:0 13px;
     font-weight:700;
      border-radius:20px;
      letter-spacing:2px;
      font-family: Josefin Slab;
      background-size: cover;
       background:url(http://8theme.com/demo/xstore/coctails/wp-content/uploads/sites/44/2017/07/subscribe-bg.png) no-repeat;

}
.copyrights-widget img:hover{
     -webkit-filter: grayscale(0%);
    filter: grayscale(0%);
}
 .copyrights-widget img {
    -webkit-filter: grayscale(100%);
    filter: grayscale(100%);
}
footer.footer{
    border-top:1px solid #ececec;
    padding-bottom:0;
}
footer.footer > .container {
    border-bottom:1px solid #ececec;
}
.footer-copyrights .textwidget{
    color:#464646;
}
.subscribe-block .et-follow-buttons a{
    font-size:17px;
    padding:0 8px !important;
}
.footer-copyrights-right img:hover{
    opacity:1;
}
.single-post .share-post{
    line-height:33px;
}

.subscribe-block .et-follow-buttons a i:hover{
background: linear-gradient(38deg, rgba(249,90,89,1) 0%, rgba(250,200,86,1) 73%, rgba(250,200,86,1) 100%); /* w3c */
 -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}
.share-post .menu-social-icons li a:hover{
    display: inline-block;
    padding: 10px 9px;
    width: 36px;
    height: 35px;
    background: #f9a058;
    margin-left: 3px;
    border-radius: 100px;
    color: #fff;
}
.share-post .menu-social-icons li a{
    display: inline-block;
    padding: 10px 9px;
    width: 36px;
    height: 35px;
    margin-left: 3px;
    border-radius: 100px;
}
.vc_gitem-zone.vc_gitem-zone-b:before{
    background:#fff;
    opacity:0.7;
}
.single-post .widget.null-instagram-feed{
    display:none;
}
.blog .wp-picture.blog-hover-zoom{
    margin-top:60px;
}
.single-post .form-submit{
    margin-bottom:120px;
}
.single-post .meta-post.et-shortcode{
    margin-top:-10px;
    margin-bottom:20px;
}
@media only screen and ( min-width:1899px ){
.banner-left_block{
    padding-left:170px;
}
.home-banner-right{
    padding-right:170px;
}
}
@media only screen and ( max-width:991px ){
    .fixed-header .container {
       padding-top: 10px;
       padding-bottom: 10px;
    }
}
@media only screen and ( max-width:481px ){
    .wpb_widgetised_column .widget-container.null-instagram-feed .instagram-size-thumbnail li {
       float: none;
    }
    .vc_gitem-zone-mini .vc_col-sm-10.vc_gitem-col{
        width:75% !important;
    }
     .vc_gitem-zone-mini .vc_col-sm-2.vc_gitem-col{
        width:24% !important;
    }
    .banner-mask-hover .vc_column-inner{
        margin-bottom:110px !important;
    }
     .banner-mask-hover h2{
        margin-top:-25px !important;
    }
    .banner-mask-hover .et-follow-buttons {
    margin-top: -98px !important;
    }
   body .content-article .read-more {
    padding: .8em 8em !important;
    }
    .page-heading .title {
       font-size: 40px !important;
    }
    .menu-block *{
        text-align:center !important;
    }
    .menu-block p.finger{
        margin-top:-15px !important;
    }
    footer.footer .responsive-block{
        margin-bottom:20px !important;
    }
    footer.footer .wpcf7 p{
        padding-top:0 !important;
    }
    body footer.footer input[type="email"]{
        text-align:left !important;
        width:95% !important;
         margin-bottom: 75px !important;
    }
    footer.footer .vc_custom_heading{
        margin-bottom:25px !important;
    }
    body .responsive-margin-bottom{
        margin-bottom:40px !important;
    }
    .footer-social .vc_column-inner{
        margin-left:0 !important;
        margin-bottom:40px;
    }
  body footer.footer *{
        text-align:center !important;
    }
  body .responsive-text{
      text-align:center !important;
  }
  footer.footer .footer-menu{
      width:100%;
      text-align:center !important;
  }
  .null-instagram-feed .instagram-pics li {
    padding: 0.1em !important;
  }

 body .home-blogs   .content-article,
 body.home  .home-blogs  .content-article p{
      font-size:16px !important;
  }
  
  .hover-bg > .vc_column-inner{
      padding-left:15px !important;
      padding-right:15px !important;
  }
  body .left-img-coctail{
      margin-left:0 !important;
  }
  .menu-block > .vc_column-inner{
      margin-right:0 !important;
      margin-left:0 !important;
      margin-bottom:60px;
  }
  body .container.content-page{
      margin-top:0 !important;
  }
  
  body .page-heading{
      padding-top: 80px;
    padding-bottom: 80px;
     background-size:cover !important;
    
  }
  
  
  
  
  
}
