custom_css:
/*Top panel*/
.top-panel-open{
    color:#424242;
}
/*header*/
header.main-header ul li a{
    letter-spacing:1px;
}
.languages-area ul li::after{
    color:#5d5d5d;
}
 .content-page {
    padding: 0 !important;
}
.container-wrapper {
    border-bottom: none !important;
}
.archive .products-list div.product{
    margin-bottom:50px;
}

.home .slide-icon{
    padding:7px 10px !important;
    background:#2b2b2b;
    width: 38px !important;
    height: 38px !important;
    line-height: 24px !important;
    display:block;
}
.home .tp-mask-wrap .rev-btn{
  padding:20px 45px !important;  
}

.page-heading .container{
    padding:35px 15px;
    border-top:1px solid #1b1b1b;
    border-bottom:1px solid #1b1b1b;
}
.center{
    text-align:center;
}
.post-type-archive .page-heading {
    padding-bottom: 0;
    margin-bottom: 0;
}
/*Wishlist*/
body.single-product .product-information a.add_to_wishlist::before,
body.single-product .form-submit input[type=submit],
body.woocommerce-checkout  input[type=submit]{
    background-color:transparent !important;
}
.content-page,
body .submit{
    background:transparent !important;
}
.meta-post-timeline{
    background:transparent !important;
    border:none !important;
}
.post-categories{
    display:none;
}
body .btn.active{
    background:transparent !important;
}
/*Contact form*/
.container-form input[type="text"],
.container-form input[type="email"],
.container-form input[type="password"],
.container-form textarea{
    width:100%;
    border-top: none;
    border-right: none;
    border-left: none;
    border-color:#353535;
    font-size:12px;
    background:transparent;
}
body .page-wrapper{
    background:transparent !important;
}
input[type="text"]:focus, input[type="number"]:focus, input[type="email"]:focus, input[type="search"]:focus, input[type="password"]:focus, input[type="tel"]:focus, textarea:focus, textarea.form-control:focus {
    border-bottom:2px solid #4e4e4e;
}
.container-form textarea{
    height:100px;
    margin-bottom:35px;
}
.container-form input[type="submit"]{
    font-size:14px;
    text-transform:uppercase;
    height:50px;
}
.wc-proceed-to-checkout a{
    background:transparent !important;
    border:none;
}
.page-id-4424 .mpc-tabs__nav ul {
    margin:0 auto 4vw;
}
.mpc-tabs__nav ul {
    display: table;
    min-width: 100px;
   width:inherit;
   border: 2px solid #787878;
   margin:0 auto 30px auto;
}
input[type="submit"],
.btn-gellary-home,
.gallary_tabs li.mpc-active,
.home-tabs li.mpc-active, 
.btn-gellary,
.mpc-timeline-item:hover,
.add_to_cart_button,
.btn-checkout,
.wc-proceed-to-checkout,
.btn.active{
    border: 2px solid #1cbffa;
    border-image: linear-gradient(150deg, rgb(250,21,65) 18%, rgb(125,8,186) 58%, rgb(54,0,255) 78%, rgb(54,0,255) 78%);
    border-image-slice: 1;
    background:transparent;
}
.single-post .meta-post-timeline{
    display:none;
}
.back-top span::before{
    color:#fff;
}
.et-timer .time-block span{
    font-size:40px;
    padding-top:16px;
}
input[type="submit"]:hover,
.btn-gellary-home:hover,
.home .center .banner:hover .btn-gellary-home,
.add_to_cart_button:hover,
.page-numbers .current,
.switch-grid.switcher-active a,
.switch-list.switcher-active a,
.switch-grid a:hover,
.switch-list a:hover,
.page-numbers a:hover,
.btn-checkout,
.wc-proceed-to-checkout:hover,
.btn-view-cart,
.show-quickly:hover,
.archive .yith-wcwl-add-to-wishlist a:hover,
.home .yith-wcwl-add-to-wishlist a:hover,
.footer-product .yith-wcwl-add-to-wishlist a:hover,
/*.home .tp-mask-wrap .rev-btn:hover,*/
.btn.active:hover,
.back-top{
    background: linear-gradient(150deg, rgb(250,21,65) 18%, rgb(125,8,186) 58%, rgb(54,0,255) 78%, rgb(54,0,255) 78%) !important;
    border-image-slice: 1;
}
.page-numbers li a,
.page-numbers li span{
    border:none !important;
}
/*Page Product*/
.woocommerce-tabs li.active span{
    color:#fff;
}
.woocommerce-tabs li.active span::after{
    display:none;
}
.woocommerce-tabs li a{
     color:#3b3b3b;
}
.qty-span{
    color:#fff !important;
}
.time-day{
    font-size:44px !important;
    font-family: Montserrat;
    font-weight: 700;
    margin-top:-5px !important;
}
.time-mon{
    font-size:16px !important;
    font-family: Montserrat;
    font-weight: 700;
    color:#fff !important;
}

/*Products Title (related product)*/
.single-product .products-title{
    margin-bottom:40px !important;
}
.single-product .products-title span{
    font-size: 50px;
    color: #ffffff;
    line-height: 50px;
    text-align: center;
    font-family: Montserrat;
    font-weight: 700;
    font-style: normal;
}
.et-follow-buttons a i:hover,
.star-rating span::before,
.home-tabs .mpc-tooltip,
.gradient_slider-home,
.time-day{
  background: linear-gradient(150deg, rgb(250,21,65) 18%, rgb(125,8,186) 58%, rgb(54,0,255) 78%, rgb(54,0,255) 78%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}
/*Shop pagination*/
.after-shop-loop,
.single-product .owl-carousel{
    margin-bottom:100px;
}
.filter-wrap {
    border-top: none;
    border-bottom: 1px solid #1b1b1b;
}
/*Image Grid Hover*/
.mpc-overlay--slide-up .mpc-grid__image:hover .mpc-item-overlay{height:32% !important;margin-top:51% !important;}
/*Color social link*/
footer .et-follow-buttons.icons-filled a i,
.map-info a i{
    color: #a4a4a4 !important;
}
footer .mpc-textblock a{
    color:#fff !important;
}
footer ul li a{
    font-weight:700;
    letter-spacing:1px;
    font-size:13px !important;
}
footer .menu-element.horizontal li {
    padding: 0 32px 0 0;

}

custom_css_mobile:
.hover-h2 img,
.separator{
    display:none;
}
.responsive-center{
    text-align:center !important;
}
.mpc-tabs__nav ul{
    border:none;
}
.et-follow-buttons.icons-filled a{
    margin-right:0px;
}
.et-timer .time-block{
    margin: 0 6px !important;
    font-size:10px !important;
    padding-top:12px !important;
}
.et-timer .time-block span{
    font-size:18px;
    padding-top:0 !important;
}
/*icon*/

.icon-box_section .mpc-icon{
    float:left;
    width:8%;
    margin-top:10px !important;
}
.icon-box_section .mpc-icon-column__content-wrap{
    width:90%;
    float:right;
    text-align:left !important;
}
.icon-box_section .location .mpc-icon-column__content p,
.icon-box_section .mpc-icon-column__content .mpc-icon-column__description{
    text-align:left !important;
}
.header-logo img {
    max-width:50px !important;
}
