custom_css:
/*top-bar*/
.top-bar.topbar-color-white {
    border-bottom: none;
}
.topbar-widget.widget_text p{
    font-family:Kanit;
    font-weight:200;
    color:#fff;
    font-size:12px;
    text-transform:uppercase;
}
.topbar-widget.widget_text p span,
.top-bar.topbar-color-white .topbar-widget.etheme_widget_socials .et-follow-buttons a{
  color:#aaaaaa;
}
.topbar-widget.etheme_widget_socials .et-follow-buttons a {
   border-left: none; 
}
.menu-social-icons i{
  color:#fff;
}
.menu-social-icons i:hover,
.media-heading{
  color:#da3d46;
}

/*header*/
.fixed-header .header-logo img{
    max-width:50px !important;
}
.fixed-header.fixed-enabled .nav-sublist-dropdown {
    top: 60px !important;
}
.page-heading{
    position:relative;
    z-index:2;
}
.page-id-26 .page-heading{
    background-image: url(http://8theme.com/demo/xstore/games/wp-content/uploads/sites/48/2017/09/bredcrambs-bg-1.png);
}
.header-double-menu .menu-wrapper .menu > li {
    padding: 0px 15px;
    border-right: 1px solid #cb4d55;
}
.header-double-menu #menu-right-menu li:last-child,
.header-double-menu #menu-left-menu li:last-child{
    border-right:none;
}
.fixed-header{
    border-top:none;
}
.header-wrapper.header-double-menu .container-wrapper {
    background-image: url(http://8theme.com/demo/xstore/games/wp-content/uploads/sites/48/2017/08/bg-header.png);
    background-repeat: no-repeat;
    background-position: center;
    border-bottom:none;
}








/*blog*/

.share-post,
.author-info,
.related-posts .title, .comment-reply-title{
    border-color:#261f1f !important;
}
.blog .post-heading h2 {
    margin-bottom: 0.5em;
    line-height:23px;
}
.author-info .media-heading {
    text-transform: uppercase;
}
.et_coutn-posts{
    display:none;
}
.blog .wp-picture.blog-hover-default{
    margin-top:40px;
}
.articles-pagination .page-numbers{
    border:none;
}
.blog .btn-read-more:hover{
    background: linear-gradient(to bottom, #a90c15 100%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%) !important;
}
.blog .btn-read-more{
    border-radius: 50px;
    font-size: 18px;
    padding: 1.6em 3.2em !important;
    font-weight: 500;
    border: none !important;
    background: linear-gradient(to bottom, #a90c15 0%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%);
}
.blog .post-heading {
    margin-bottom: 0.8em;
}
.blog .content-article a{
    display:none;
}
.home .blog-mask{
    display:none !important;
}
.read-more.btn:hover{
   background: linear-gradient(to bottom, #a90c15 100%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%) !important;
}
.post-categories{
    padding: 0.5em 2.5em 0.5em 2.5em !important;
    border-radius:0 20px 20px 0;
    background: linear-gradient(to bottom, #a90c15 0%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%);
}
.post-heading h2 a{
    font-size:18px;
}
.entry-date.published.updated:before,
.post-comments-count:before,
.wc-tabs.tabs-nav .tab-title.opened span,
.wc-tabs.tabs-nav .tab-title:hover span,
.single-product .product-information-inner .woocommerce-Price-amount,
.quick-view-info .woocommerce-Price-amount{
background:
    linear-gradient(to bottom, rgba(169,12,20,1) 0%, rgba(207,80,91,1) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
     color:transparent;
}
.meta-post{
    font-size:16px;
    background: linear-gradient(to bottom, rgba(169,12,20,1) 0%, rgba(207,80,91,1) 100%);
     -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}
.read-more.btn {
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: normal;
    padding: 0.6em 2.1em;
    font-size: 16px;
    border:none;
    border-radius: 40px;
    background: linear-gradient(to bottom, #a90c15 0%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%);
}

/*Shop*/

body .shop_table.woocommerce-checkout-review-order-table *{
    border-color:#2f2f2f !important;
}
.checkout_coupon input.btn{
    border:none;
}
.button.btn-checkout.wc-forward,
.wc-proceed-to-checkout a,
.form-row.place-order input,
.btn.remove-item:hover,
.checkout_coupon input.btn:hover,
.button.btn-view-wishlist,
.remove.remove_from_wishlist:hover,
.woocommerce-Button.button,
.cart-empty.empty-cart-block .btn{
   border:none !important;
   background: linear-gradient(to bottom, #a90c15 0%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%);
}

.form-submit .submit:hover,
.product-information-inner .add_to_cart_button:hover{
    background: linear-gradient(to bottom, #a90c15 100%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%) !important;
}
.form-submit .submit,
.product-information-inner .add_to_cart_button,
.cart .add_to_cart_button{
    border-radius:30px;
    font-size:18px;
    font-weight:500;
    height: 60px;
    min-width: 200px;
    border:none;
    background: linear-gradient(to bottom, #a90c15 0%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%);
}


td ,
tr,
th,
.shop_table,
.block-title,
.woocommerce-billing-fields .step-title,
.woocommerce-shipping-fields .step-title,
.woocommerce table.wishlist_table tbody td,
.woocommerce-wishlist.woocommerce div.page-wrapper table.wishlist_table tbody td *,
.wishlist-dropdown.product_list_widget *,
.cart-popup-container *,
.woocommerce-account header.title,
#customer_login .col-1,
#customer_login h2,
.woocommerce-account .woocommerce h2{
   border-color:#261f1f !important;
}

.star-rating span:before{
    color:#fcd20c !important;
}
.product-details .woocommerce-Price-amount{
    font-size: 23px;
    color: #fff;
    font-weight: 500;
}
.yith-wcwl-add-to-wishlist .yith-wcwl-add-button .add_to_wishlist:before {
    top: 34% !important;
}
.single-product .products-title span {
    font-size: 40px;
    font-weight:600;
}
.content-product.product-inview:hover .product-image-wrapper img{
    -webkit-filter: grayscale(100%); /* Safari 6.0 - 9.0 */
    filter: grayscale(100%);
}
body .tagged_as a{
    color: #fff !important;
    text-transform: capitalize !important;
    font-weight: 200 !important;
    font-size: 16px !important;
    
}
.single-product .product-information-inner .woocommerce-Price-amount,
.quick-view-info .woocommerce-Price-amount{
    font-size:40px;
    font-weight:500;
}
.quick-view-info .share-title{
    color:#fff !important;
}
.quick-view-info .add_to_cart_button{
    margin-top:25px;
    margin-bottom:15px;
}
.single.single-product .tabs-after_content{
    margin-top:70px;
}
.qty-span{
    margin-bottom:-3px;
}
.product-information .share-title,
.qty-span,
.product-information-inner .add_to_wishlist{
    color: #fff;
    font-size: 16px !important;
    text-transform: capitalize;
    font-weight: 200;
}
.product-information-inner h4.title{
    font-size:30px;
}

.product-information-inner{
    margin-top:170px;
}
.product-information .product_meta > span, .product-info-wrapper .product_meta > span, .product-summary-center .product_meta > span {
    display: block;
    margin-bottom: 10px;
    font-size: 16px;
}
.pagination-cubic ul li span,
.pagination-cubic ul li a{
    font-size:11px;
    padding: 0 16px;
     max-width:35px !important;
}
.product-details .products-page-cats a{
    font-size: 16px;
    color: #da3d46;
    font-weight: 200;
    text-transform: initial;
    letter-spacing: 1px;
}
.product-details .product-title a{
    font-size: 23px;
    margin-bottom:15px;
}
.swiper-custom-right,
.swiper-custom-left{
    background:transparent !important;
}
.swiper-custom-right:before,
.swiper-custom-left:before{
    color:#fff;
}
.filter-wrap,
.product-share {
    border-color:#261f1f !important;
}
.quantity.buttons_added span:hover{
    border-color:#261f1f !important;
}
.quantity.buttons_added span:before{
    color:#fff !important;
}
.quantity.buttons_added span:hover:before{
    color:#a90c15 !important;
}

    
.minus,
.plus,
.minus + .input-text{
    background:transparent !important;
    border:1px solid #261f1f !important;
    color:#fff !important;
}
.minus{
    margin-right:-4px;
}
.plus{
    margin-left:-4px;
}
.input-text{
    margin:0 !important;
}
.single-product .product-information-inner .products-page-cats{
    display:none;
}

.after-shop-loop,
.articles-pagination {
    border-color:#261f1f !important;
    margin-top:100px;
}
.after-shop-loop .woocommerce-result-count{
    display:none;
}
.pagination-cubic ul li a, .pagination-cubic ul li span{
    border:none;
    background:transparent;
    font-weight:200;
}
.switch-list a:before,
.switch-grid a:before,
.products-per-page span{
    color:#fff !important; 
   
}
.product-information .yith-wcwl-add-to-wishlist a:not(.alt):before, .product-info-wrapper .yith-wcwl-add-to-wishlist a:not(.alt):before, .product-summary-center .yith-wcwl-add-to-wishlist a:not(.alt):before {
    background-color: #0b1014 !important;
}
.filter-wrap .products-per-page, .view-switcher, .shop-filters-area .sidebar-widget ul li {
    border-color: #261f1f;
}
.archive .content-product{
    margin-bottom:70px;
}
.single-product .tab-title:before {
    content: "";
    width: 6px;
    height: 6px;
    background: #261f1f;
    display: block;
    position: absolute;
    right: 0;
    transform: translateY(-50%);
    top: 50%;
}
.single-product li:last-child a.tab-title::before {
    display:none;
}
.wc-tabs.tabs-nav .tab-title span:after{
    display:none;
}
.wc-tabs.tabs-nav .tab-title span{
    font-size:23px;
    padding:0 30px;
}
.page-numbers.current,
.pagination-cubic ul li a:hover,
.switch-grid.switcher-active a,
.switch-grid:hover a,
.switch-list:hover a,
.switch-list.switcher-active a,
.open-filters-btn .btn,
.articles-pagination .page-numbers:hover{
    background: linear-gradient(to bottom, #a90c15 0%,#da3d46 100%,#da3d46 100%,#2989d8 100%,#2989d8 100%,#2989d8 100%,#000000 100%) !important;
}
.open-filters-btn .btn{
    border:none;
    width:110px;
    font-size: 0.82em;
}
div.fancy-select div.trigger, .view-switcher .switch-list a, .view-switcher .switch-grid a, div.fancy-select div.trigger.open, div.fancy-select ul.options {
    background-color: #050709;
    color:#fff;
}



/*footer*/
.widget_product_categories.sidebar-widget > ul > li:last-child > a, .wpb_widgetised_column .widget_product_categories > ul > li:last-child > a, .widget_nav_menu.sidebar-widget > ul > li:last-child > a {
    border-bottom: none !important;
}
.wpb_widgetised_column .widget_product_categories ul li a:hover{
    color:#fff !important;
}
 .wpb_widgetised_column .widget_product_categories ul li a{
    font-weight: 400;
    border-bottom: none;
    color: #828384 !important;
    text-transform: uppercase;
    padding-top: 0;
    padding-bottom: 16px;
    font-size: 14px !important;
 }
 .et-mailchimp input[type="submit"]{
    margin-right:10px;
     
 }
.footer .mc4wp-form .et-mailchimp, .prefooter .mc4wp-form .et-mailchimp {
    margin-bottom: 10px;
}
.mc4wp-form .et-mailchimp input[type="email"] {
    background-color: transparent;
    border-color:#444547;
}


.footer-mailchimp-text{
    margin-bottom:8px;
    font-size:14px;
    font-weight:400;
}

.null-instagram-feed .instagram-pics.instagram-columns-4 li {
    width: 23%;
    padding: 0 5px 10px 0;
}
footer.footer .wpb_widgetised_column.wpb_content_element{
    margin-bottom: 26px;
}
footer.footer .menu-mobile-menu-container li a{
    color:#828384;
    font-size:14px;
    font-weight:400;
    padding-bottom:15px;
}
footer.footer .menu-mobile-menu-container li a:hover{
    color:#fff !important;
}
footer.footer input[type="email"]::-webkit-input-placeholder {
    color: #a4a4a4;
}

@media only screen and (min-width: 1900px) {
    .page-heading{
      background-position: 50% 100% !important;
    }
}

@media only screen and (max-width: 480px) {
    .single-post .content-article.entry-content h2{
        font-size:24px !important;
    }
    .page-heading h1{
        font-size:26px !important;
        line-height:30px !important;
    }
    .banner-home > .vc_column-inner {
        margin-right: 15px !important;
    }
    .wrapper-banners {
       top: 0px !important; 
       min-width: 0px !important; 
       margin-left: 0px !important; 
    }
    .fixed-header .header-logo img{
        max-width:30px !important;
    }
    .product-view-mask3 .footer-product .yith-wcwl-add-to-wishlist{
        margin-left:0 !important;
        margin-right:0 !important;
    }
    body div.section-logo{
       margin-bottom: -91px !important;
       padding-bottom: 100px !important;
    }
    
    .mpc-button.mpc-transition.mpc-typography--default.mpc-inited{
        width:70px !important;
        height:70px !important;
    }
    .home .mpc-lightbox.mpc-transition.mpc-magnific-popup {
        padding: 9px 9px 1px 9px !important;
        top: 6%;
        left: 38%;
     }
    .home .mpc-lightbox  .mpc-button__icon {
        padding-right: 35px !important;
        padding-top: 20px !important;
        padding-bottom: 31px !important;
        padding-left: 23px !important;
        font-size: 22px !important;;
   }
    body .home-img-right-block > .vc_column-inner > .wpb_wrapper {
        margin-right: 0px !important;
        margin-top: 240px;
   }
    .read-more{
        margin:40px auto 100px auto !important;
    }
    .home article .post-comments-count{
        margin-right:30px;
    }
    .resp-margin-top{
        margin-top:200px;
    }
    .recomendet-date{
        padding-left:0 !important;
    }
    .recomendet-date:before,
   .excerpt-first:before{
       content: url(http://8theme.com/demo/xstore/games/wp-content/uploads/sites/48/2017/09/img-for-excerpt-for-banner.png) !important;
       margin-right: 15px;
       position: relative;
       bottom: 6px;
    }
    .recomendet-date:after,
    .excerpt-first:after{
       content: url(http://8theme.com/demo/xstore/games/wp-content/uploads/sites/48/2017/09/img-for-excerpt-for-banner.png) !important;
       margin-left: 15px;
       position: relative;
       bottom: 6px;
    }
    .border{
        border:1px solid #474546  !important;
    }
    .responsive-margin-bottom{
        margin-bottom:100px;
    }
    body div.section-logo {
        background-image:none !important;
    }
    
    .mpc-testimonial__content .mpc-testimonial__description p:last-child{
         padding: 0 35px !important;
    }
    .responsive-center *,
    .home article.blog-post *{
        text-align:center !important;
    }
    .page-heading{
      padding-top: 9em !important;
      padding-bottom: 5em !important;
    }
    body footer.footer  .et-mailchimp p input[type="email"]{
        text-align:left !important;
    }
    footer.footer *{
        text-align:center !important;
    }
    footer.footer {
        padding-top:4em;
        background-image:none !important;
        background-color:#050709;
    }
    .footer-social .vc_column-inner{
        margin-left:0 !important;
        margin-bottom:50px;
    }
    .footer-menu{
        width:100%;
    }
    .null-instagram-feed .instagram-pics.instagram-columns-4 li{
        width:50% !important;
    }
    .responsive-block{
        width:50%;
        float:left;
    }
}
