custom_css:
/* Base */
h3.vc_custom_heading {
    text-transform: none;
}
h2.vc_custom_heading {
    letter-spacing: 0.2px;
}
.mpc-button__title {
    letter-spacing: 0.5px;
}
/* header */
.header-wrapper.header-two-rows.header-color-white .container-wrapper {
    border: 0px;
    margin-bottom: 15px;
}
.admin-bar .fixed-header.fixed-enabled{
    border: none;
}
.fixed-header.header-color-white{
    border: none;
}
.header-wrapper .languages-area, .header-wrapper.header-two-rows .topbar-color-white .top-links ul > li > a {
    font-family: Lato;
    font-size: 11px;
    font-weight: 400;
}
.header-wrapper.header-two-rows .right-wrap .navbar-header i, .header .cart-bag {
    color: #6c4f3a;
}

.header-wrapper.header-two-rows .topbar-color-white .languages-area .menu > li.menu-parent-item::after, .header-wrapper.header-two-rows .topbar-color-white .my-account-link::after {
    opacity: 0.4;
}
.menu-wrapper .menu > li > a:after{display:none;}
.header-wrapper.header-two-rows .menu-wrapper .menu > li{margin: 0 10px;}
.header-top .total {
    font-family: Lato;
    font-size: 14px;
}

.fixed-header{
    box-shadow: 1px 1px 1px #90705a;
}
@media only screen and (max-width: 992px){
.fixed-header .navbar-header .et-wishlist-widget i, .fixed-header .cart-bag:before {
    font;
    font-size: 1.5em !important;
}
.navbar-header .et-wishlist-widget a{
    line-height: 16px;
}
.header-wrapper .navbar-header {
    margin-right: 40px;
}
.single-product .fixed-product-block .fixed-content .yith-wcwl-add-to-wishlist a:not(.alt) {
    font-size: 13px !important;
}
.single-product.woocommerce .product-information .yith-wcwl-add-to-wishlist a:not(.alt):before {
    position: initial;
}
}

/* Single product */
.single-product .carousel-area .products-title {
    display: none;
}
.single-product .tabs {
    margin-bottom: 0;
}
.single-product .product_title {
    font-size: 30px;
    letter-spacing: 0.2px;
}
.single-product .product-information .sku_wrapper {
    display: none;
}
.single-product .fixed-product-block .fixed-content .products-page-cats a {
    text-transform: none;
    font-size: 16px !important;
}
.single-product .short {
    display: none;
}
.single-product .fixed-product-block .woocommerce-product-rating {
    margin-bottom: 15px;
}
.fixed-product-block .fixed-content .share-title {
    color: #a4a4a4;
    font-size: 14px;
    font-weight: 400;
    text-transform: none;
}
.single-product .menu-social-icons i {
    color: #1c1a19;
    font-size: 14px;
}
.single-product .menu-social-icons i:hover {
    color: #90705a;
}
.single-product.woocommerce .product-information a.add_to_wishlist.button:before,.single-product.woocommerce .product-information .yith-wcwl-add-to-wishlist a:not(.alt):before {
    content: "\f08a" !important;
    font-family: FontAwesome !important;
    background-color: transparent !important;
    transition: all 0.2s linear;
    margin-right: 5px;
    font-size: 18px;
    color: #1c1a19;
    padding: 0;
    top: 0;
    height: 18px;
    line-height: 18px;
}
.single-product .fixed-product-block .fixed-content .yith-wcwl-add-to-wishlist a:not(.alt) {
    font-size: 13px;
}
.single_add_to_cart_button {
    background-color: #90705a;
    border-color: #90705a;
}
.single_add_to_cart_button:hover {
    background-color: rgba(144, 112, 90, 0.9) !important;
    border-color: rgba(144, 112, 90, 0.9) !important;
}
.single-product .tab-title span {
    font-family: Catamaran;
    letter-spacing: 0.2px;
}
.single-product  .tab-title.opened span:after {
    display: none;
}
.tabs .tab-title {
    font-weight: 700 !important;
}
.single-product .tab-content {
    padding: 0px;
}
.single_variation_wrap {
    margin-top: 0px !important;
}
.single-product .fixed-content .yith-wcwl-add-to-wishlist {
    margin-bottom: 0px;
    font-family: Lato;
}
.product-information table.variations td.label label {
    font-family: Lato;
    font-size: 14px;
    text-transform: none;
}
.single_add_to_cart_button {
    font-family: Lato;
    font-size: 14px;
    min-width: 167px;
}
.single-product .tab-title:before {
    content: "";
    width: 4px;
    height: 4px;
    background: #90705a;
    display: block;
    position: absolute;
    right: 0;
    transform: translateY(-50%);
    top: 50%;
    border-radius: 100%;
    opacity: 0.2;
}
.single-product .tab-title {
    position: relative;
}

.single-product #tab_reviews:before { 
    display:none;
}
.product-content .compare {
    font-family: Lato;
}

/* content */
.view-color-dark .button:hover, .view-color-dark .show-quickly:hover, .view-color-dark .yith-wcwl-add-button a.add_to_wishlist:hover, .view-color-dark .yith-wcwl-wishlistexistsbrowse a:hover, .view-color-dark .yith-wcwl-wishlistaddedbrowse a:hover{
    background-color: #785b47;
    border-color: #785b47;
}
.view-color-white .footer-product .button, .view-color-white .show-quickly::before, .view-color-white .yith-wcwl-add-button a.add_to_wishlist::before {
    background-color: rgb(108, 79, 58);
    color:white;
}
.view-color-white .footer-product .button:hover,.view-color-white .show-quickly:hover, .view-color-white .yith-wcwl-add-button a.add_to_wishlist:hover {
    background-color: #785b47;
    color:white;
}
.view-color-white .show-quickly::before, .view-color-white .yith-wcwl-add-button a.add_to_wishlist::before {
    background-color: transparent;
}
.products-grid .content-product .product-title a {
    font-weight: 700;
    letter-spacing: 0.2px;
}
.product-information table.variations tr:last-child td.value select {
    margin-bottom: 5px;
}
.comment-form #submit {
    background-color: transparent;
    border: none;
    color: #6c4f3a;
    font-size: 14px;
    height: 90px;
    line-height: 30px;
    font-family: Lato;
    padding-left: 42px;
    text-align: center;
    text-transform: uppercase;
    background-image: url(http://8theme.com/demo/xstore/handmade/wp-content/uploads/sites/33/2017/03/post-btn.png);
    background-repeat: no-repeat;
    background-position: left center;
}
.comment-form #submit:hover {
    color: rgba(144,112,90,0.58);
}
.single-post .header-bg-block {
    background: transparent;
}
.single-post .post-categories {
    display: none;
}
.single-post .post-heading h2 {
    text-transform: none;
}
.single-post .menu-wrapper .menu > li > a {
    color: white;
}
.single-post .header-wrapper.header-two-rows .right-wrap .navbar-header i, .single-post .header .cart-bag{
    color: white;
}
.posts-slider article .content-article p {
    letter-spacing: 0.2px;
}
.posts-slider .post-heading h2 a {
    font-family: Lato;
    text-transform: none;
}
.slider-container .post-categories {
    display: none;
}

/* contact form */
.wpcf7 .form-group input, .wpcf7-form p textarea{
    background: transparent;
    border: 0;
    border-bottom: 2px solid rgba(108, 79, 58, 0.50);
    transition: all 0.2s linear;
}
.wpcf7 .form-group input{
    height: 40px;
}
.wpcf7 .form-group input:hover,
.wpcf7 .form-group  input:focus,
.wpcf7  textarea:hover,
.wpcf7  textarea:focus{
    border-bottom-color: #6c4f3a;
}
.wpcf7 .form-group-btn input[type="submit"] {
    background-color: transparent;
    border: none;
    color: #6c4f3a;
    font-size: 13px;
    height: 90px;
    font-family: Lato;
    padding-left: 48px;
    text-align: center;
    text-transform: uppercase;
}
.wpcf7 .form-group-btn input[type="submit"]:hover {
    color: rgba(144,112,90,0.58);
    background: transparent;
}

.wpcf7 .form-group-btn {
    background-image: url(http://8theme.com/demo/xstore/handmade/wp-content/uploads/sites/33/2017/02/bg.png);
    background-repeat: no-repeat;
    background-size: contain;
    background-position: center center;
}

.post-grid .meta-post-timeline{
    position: absolute;
    left: 55px;
    top: 15px;
}
.mpc-ribbon:before{display:none;}
.content-product .product-image-wrapper .footer-product,
.view-color-dark .button,
.footer-product .show-quickly,
.footer-product .yith-wcwl-add-button a.add_to_wishlist, 
.footer-product .yith-wcwl-wishlistexistsbrowse a, .footer-product .yith-wcwl-wishlistaddedbrowse a{
    background-color: #6c4f3a;
}
.footer-product .yith-wcwl-add-button a.add_to_wishlist, 
.footer-product .yith-wcwl-wishlistexistsbrowse a, 
.footer-product .yith-wcwl-wishlistaddedbrowse a,
.content-product .product-image-wrapper .footer-product .show-quickly{
    border-color:#785b47;
}
.footer-product .yith-wcwl-wishlistexistsbrowse a:before,
.footer-product .yith-wcwl-wishlistaddedbrowse a:before{
    color:#fff !important;
}
/* footer section */
#footer-section {
    background-position: center center !important;
}
#footer-section .et-follow-buttons.buttons-size-large a {
    width: 54px;
    height: 60px;
    margin: 6px;
    line-height: 49px;
    font-size: 29px;
}
@media only screen and (max-width:992px){
    .header-wrapper.header-two-rows .container-top-wrapper{
        border-bottom:none;
    }
    .header-wrapper.header-two-rows .right-wrap .navbar-header i, 
    .header .cart-bag{
        color:#fff;
    }
}

custom_css_desktop:
.single-product .menu-social-icons {
    float: right;
}
.header-wrapper.header-two-rows .header-top .container-top-wrapper{
    padding-top:40px;
}

.menu-wrapper .menu > li > a:hover,
.menu-wrapper .menu > li.current-menu-item > a{color:#fff !important;}
