custom_css:
/*Header*/
.fixed-header{
    border-top:none;
}
.header-color-dark .cart-summ:hover .cart-bag .badge-number{
    color:#fff !important;
}
.page-heading.bc-size-large .title {
    margin-bottom: 10px;
}
.template-container .page-heading .col-md-12 {
  display: -webkit-box;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: vertical;
  -webkit-box-direction: reverse;
  -webkit-flex-direction: column-reverse;
      -ms-flex-direction: column-reverse;
          flex-direction: column-reverse;
}
.navbar-header.show-in-header{
    width:125px !important;
}
.header-xstore .menu-wrapper, .header-xstore .header-logo, .header-xstore .navbar-header {
    width: auto;
    
}
header.header  ul li.item-level-0, .fixed-header ul li.item-level-0{
    padding:0 24px !important;
}
header.header ul li a:after,
.fixed-header ul li a:after{
    margin-top:5px;
}

/*Content*/
.hover-bg .vc_column-inner{
    transition: all 0.4s ease-in-out
}
.hover-bg:hover .vc_column-inner {
    -webkit-transform: scale(1.05);
    -ms-transform: scale(1.05);
    transform: scale(1.05);
    transition: all 0.4s ease-in-out;
}
.hover-bg{
    position: relative;
    overflow: hidden;
    cursor: pointer;
    -webkit-transition: all 0.3s ease-in-out;
    transition: all 0.3s ease-in-out;
    backface-visibility: hidden;
    -webkit-backface-visibility: hidden;
}
.capitalize{
    text-transform:initial !important;
}
.letter-1{
    letter-spacing:1px;
}

/*Shop*/
body header.header .cart-widget-products li{
    padding-bottom:20px !important;
}
.archive .product-details .product-title a{
    font-size:20px;
}
.product-information table.variations td.value{
    margin-bottom:15px;
}
.quantity.buttons_added .input-text.qty.text{
    margin-left:-2px;
    margin-right:-2px;
    background:transparent !important;
    border-top:none;
    border-bottom:none;
}
.woocommerce-wishlist footer.footer {
    position:fixed !important;
    bottom:0 !important;
}
.product-share{
    margin-top:30px;
}
.single-product  .qty-span{
    margin-bottom:1px;
}
.product-information .cart + .yith-wcwl-add-to-wishlist{
    margin-bottom:0px;
}
.product-information .share-title{
    font-size:14px;
    font-weight:100;
    text-transform:capitalize;
}

.single-product .single_add_to_cart_button,
.form-submit input[type=submit]{
    font-family: "Josefin Sans";
    font-weight:100 !important;
    text-transform:capitalize !important;
    height:50px;
    width:220px;
    font-size:18px;
    border-radius:60px;
    margin-top: 20px;
}
 .single-product .single_add_to_cart_button{
    margin-top:0px !important;
}
.quantity.buttons_added .minus:before,
.quantity.buttons_added .plus:before {
    color:#302e2e !important;
}
.single-post input[type=email],
.single-post input[type=text]{
    height:52px;
}
.single-post textarea{
    height:150px;
}
.small-h,
.woocommerce-mini-cart > p{
    font-family: "Josefin Sans";
    font-weight:400;
}
.single-tags a{
    background:transparent;
}
.single-post input[type=email],
.single-post input[type=text],
.single-post textarea{
    background-color: transparent;
    border-top:none !important;
    border-left:none !important;
    border-right:none !important;
    border-bottom: 1px solid #e0e2e3;
    font-size:16px;
    color:#6e6e6e;
    font-weight:300;
    padding-left:20px;
    width:100% !important;
}
.comment-reply-title{
    text-align:center !important;
    font-family: "Josefin Sans";
     text-transform:capitalize;
     font-size:45px;
     padding-bottom: 50px;
    padding-top: 50px;
    font-weight:400;
}
.share-post .share-title{
     font-family: "Josefin Sans";
     text-transform:capitalize;
     font-size:18px;
}
.single-post .share-post .menu-social-icons a:hover{
    color:#c0c0c0;
}
.single-post .share-post .menu-social-icons a{
    color:#302e2e;
}
.single-post .comment-form input:focus,
.single-post .comment-form textarea:focus{
     border-bottom: 2px solid #302e2e;
}

.single-post .form-submit{
    text-align:center !important;
    margin-bottom:60px;
}
.minus{
    background:transparent !important;
    border-right:1px solid #ebebeb !important;
}
.plus{
    background:transparent !important;
    border-left:1px solid #ebebeb !important;
}
.quantity.buttons_added{
    border:1px solid #ebebeb;
}
.product-information select{
    font-size: 12px;
    color: #6e6e6e;
    border:1px solid #ebebeb;
    width:240px;
}
.single-product .col-lg-6.col-md-6.col-sm-12.product-images{
    margin-bottom:-280px !important;
}
.sku_wrapper{
    display:none !important;
}
.product-information .menu-social-icons li a:hover,
.product-information .share-title{
    color:#c0c0c0 !important;
}
.product-information .menu-social-icons li a {
    color:#302e2e !important;
}
.product-information table.variations td.label label,
.product-content .compare,
.single-product .product-information .yith-wcwl-add-to-wishlist a:not(.alt),
.single-product .qty-span{
    font-family: "Josefin Sans";
    font-size:18px;
    font-weight:400;
    text-transform:capitalize;
}
.single-product-wide .product-information-inner ins .woocommerce-Price-amount.amount {
    font-weight: 400;
}
.single-product .woocommerce-breadcrumb a:first-child + .delimeter i{
    font-size:13px !important;
}
.single-product .woocommerce-breadcrumb a{
    font-size:16px;
}
.single-product .product-information .price{
     font-family: "Josefin Sans";
     font-weight:400;
}
.single-product .product-information .price del .amount{
    font-size:25px !important;
   
}
.page-heading .title{
    text-transform:capitalize !important;
}
.products-grid .content-product .price del,
.products-grid .content-product .price ins{
    font-weight:400 !important;
}
.products-grid .content-product .price {
    font-size:20px;
    font-family: "Josefin Sans";
    margin-bottom: 50px;
}
.content-product .star-rating{
    margin: 0px auto 12px !important;
}
.single-product-wide .product-information-inner .product_title{
     font-family: "Josefin Sans";
     font-weight:400;
}
.product-details .product-title a{
    font-family: "Josefin Sans";
    font-size: 28px;
    text-transform: capitalize;
    font-weight:400 !important;
    margin-top:14px;
    margin-bottom:15px !important;
}
.content-product .products-page-cats a{
    color:#c0c0c0;
    font-weight: 200;
    text-transform: uppercase;
    letter-spacing: 0px;
    font-size: 14px;
}
.pagination-cubic .page-numbers li a{
    background:#fff;
    border:1px solid #e3e3e3;
}
.pagination-cubic .page-numbers li a,
.pagination-cubic .page-numbers li span{
    padding: 0px 14px;
    font-size:14px;
    max-width:36px;
}
body .filter-wrap .products-per-page span{
    font-family: "Josefin Sans";
    font-size: 18px;
    font-weight: 400;
    text-transform: capitalize;
}



/*blog*/

.page-heading h1::first-letter{
    text-transform: uppercase !important;
}
.page-heading h1 span{
    text-transform: lowercase;
}
.single-post .share-post .menu-social-icons{
    margin-top:6px;
}
.single-post article.blog-post{
     margin-bottom: 0px; 
}
.et_load-posts .btn a {
    color: #fff;
    padding: 0 1.95em;
    text-transform: capitalize;
    font-family: "Josefin Sans";
    font-weight: 100;
    font-size:18px;
    line-height: 48px;
}
.et_load-posts.button-loading .btn{
    background: #302e2e;
    border-radius: 60px;
    border-color: #302e2e;
    height:48px;
}
.blog article p{
    font-family: Montserrat;
    line-height: 25px;
    color: #c0c0c0;
}
.first-block .vc_column-inner{
    height:1040px;
}
article.post-grid .read-more {
    margin-top: 20px;
}
.post-heading {
    margin-bottom: 1.1em;
}
.post-comments-count span,
.views-count{
    color:#c0c0c0 !important;
}
.views-count:before{
    color:#302e2e;
}
.meta-post{
    font-size:16px;
}
.post-heading h2 a{
    text-transform: lowercase;
}
.post-heading h2{
    font-size:24px !important;
    line-height:1.3em;
    letter-spacing:0;
    font-family: "Josefin Sans";
    
}
.single-post .post-heading{
    display:none;
}
.post-heading h2::first-letter{
    text-transform: uppercase !important;
}
.post-categories{
        padding: 0.4em 1.6em !important;
}
.post-categories a{
    letter-spacing:1px;
    font-size:11px;
}
.blog article *{
   text-align:center !important;
}
.read-more{
    margin:0 auto;
    font-family: "Josefin Sans";
    font-weight:700;
}

.et-header-overlap .woocommerce-message {
   margin-top: 105px;
}

/*Footer*/
footer.footer ul li a{
   margin:0px 23px !important;         
}
footer.footer ul li a{
    font-size:14px !important;
}
footer.footer .et-follow-buttons a{
    padding:0 11px !important;
}
footer.footer .menu-main-menu-container ul.menu li a{
    display: inline-block;
    color:#302e2e !important;
    line-height: 26px;
    text-decoration:none;
    cursor: pointer;
    position:relative;
    font-weight:600;
}
footer.footer .menu-main-menu-container ul.menu li a::after{
    background-color: #302e2e;
    display: block;
    content: "";
    height: 2px;
    width: 0%;
    left:50%;
    position:absolute;
    -webkit-transition: width .3s ease-in-out;
    -moz--transition: width .3s ease-in-out;
    transition: width .3s ease-in-out;
    -webkit-transform:translateX(-50%);
    -moz-transform:translateX(-50%);
    transform:translateX(-50%);
}
footer.footer .menu-main-menu-container ul.menu li.current_page_item a::after,
footer.footer .menu-main-menu-container ul.menu li a:hover::after{
    width: 100%;
}

footer.footer {
    position:relative;
    z-index:20;
}
@media screen and (max-width: 768px) {
    .woocommerce-cart table.cart .product-details a{
        font-size: 11px;
        line-height: 1.5em;
        margin-bottom: -4px;
    }
    .page-heading .title{
        font-size:38px !important;
        line-height:40px !important;
    }
    .meta-post {
      font-size: 15px;
     }
    .ipad-responsive{
        width:50% !important;
    }
    .home-section-1 .vc_column-inner, .home-section-2 .vc_column-inner {
       max-height: 300px !important;
    }
    
    .single-product .col-lg-6.col-md-6.col-sm-12.product-images {
       margin-bottom: 0px !important;
     }
     .single-product .single_add_to_cart_button {
     margin-top: 15px !important; 
   }
   .comment-reply-title {
    font-size: 32px;
    }

    footer.footer ul li a {
       margin: 0px 15px !important;
     }
    .mobile-menu-wrapper .header-search.act-default .search-btn i {
       line-height: 1.4;
       padding-right: 5px;
       overflow: visible; 
    }
    .black-block .mpc-list__title{
        text-align:left !important;
    }
    .vc_separator{
        display:none !important;
    }
    .black-block .vc_column-inner{
        padding:60px 0 20px;
    }
    .white-block .vc_column-inner,
    .black-block .vc_column-inner {
        padding-right: 15px !important;
        text-align:center;
        padding-left: 15px !important;
    }
    .about-us-section .about-us-right .vc_column-inner {
        padding-top: 140px !important;
    }
    .about-us-section h2{
        font-size:38px !important;
    }
    .white-block h2,
    .white-block .wpb_text_column p{
        text-align:center !important;
    }

     body .template-container  .sidebar-position-without .first-block .vc_column-inner,
     body .template-container  .sidebar-position-without .two-block .vc_column-inner,
     body .template-container  .sidebar-position-without .three-block .vc_column-inner{
       max-height: 500px !important;
     }
    .about-us-section * {
        text-align:center !important;
    }
    .about-us-section .vc_column-inner {
        padding-top: 340px !important;
        padding-right: 15px !important;
        padding-left: 15px !important;
    }
   .home .wpcf7-form-control.wpcf7-submit{
        margin-left:15px !important;
    }
    .home .wpcf7{
        text-align:center !important;
    }
    body .block-informations .et-follow-buttons.buttons-size-normal {
       margin-left: 0px !important;
       margin-bottom:60px;
    }
   body  .section_info{
        padding-left:30px !important;
        padding-right:30px !important;
    }
    footer.footer *{
        text-align:center !important;
    }
    .footer .footer-widgets ul li {
      padding:0;
     }
     .menu-element.horizontal.menu-align-center{
         margin-bottom:30px;
     }
    body .responsive-text *,
    .responsive-text{
        text-align:center !important;
    }
    footer.footer ul li a{
        color:#302e2e !important;
    }
    .responsive-margin-bottom,
    .white-block .mpc-button{
        margin-bottom:50px;
    }
    .page-heading{
        padding-top: 7em !important;
       padding-bottom: 2em !important;
    }
}
@media screen and (max-width: 768px) {
    .ipad-responsive{
        width:100% !important;
    }
}
@media screen and (min-width: 1700px) {
    .blog article p{
      padding: 0px 40px;
    }
}
@media screen and (min-width: 1900px) {
   body .block-informations .vc_column-inner{
       padding-left:200px !important;
   }
}
@media screen and (min-width: 2100px) {
   body .block-informations .vc_column-inner{
       padding-left:300px !important;
   }
}
