custom_css:
::selection {background: #9f3659;}
/*Header*/

.breadcrumbs-type-disable.et-header-not-overlap:not(.home) .content-page {
    margin-top: 0px;
}
.fixed-header{
    border:none;
}
.header-color-dark .et-wishlist-widget .wishlist-count,
.header-color-dark .shopping-container.ico-design-4 .cart-bag .badge-number{
    background:#9f3659 !important;
}
body .page-heading {
    margin-bottom:100px;
    box-shadow: 1px 1px 38px #f6f6f6 inset;
}
.single-post .page-heading{
    display:none;
}
.single-post .menu-social-icons li{
    margin-bottom:0px !important;
}
.blog-post.post-single .share-post ul li a:hover{
    color:#9f3659;
}
.footer-product .yith-wcwl-add-button .add_to_wishlist,
.footer-product .add_to_cart_button,
.footer-product .show-quickly{
    background:#231f20 !important;
}
.footer-product .yith-wcwl-add-button .add_to_wishlist:hover,
.footer-product .add_to_cart_button:hover,
.footer-product .show-quickly:hover{
    background:#9f3659 !important;
}

.revolution-section{
    box-shadow: 1px 1px 40px #f6f6f6 inset;
    border-top:1px solid #eeedee;
    border-bottom:1px solid #eeedee;
}
.single-post .share-post{
  padding: 2em 0 1.7em;
  text-align:left;
}
.slider-container .title{
    text-align:left;
    font-size:19px !important;
}
.fullscreen-menu .hamburger-icon span{
    background:#231f20 !important;
}
.fullscreen-menu{
    background:rgba(255, 255, 255, 0.9) !important;
}
.fullscreen-menu .menu > li{
    margin-bottom:50px;
}
.fullscreen-menu .menu > li > a:hover{
    color:#9f3659 !important;
}
.fullscreen-menu .menu > li > a{
    color:#231f20 !important;
    font-family:Lora;
}
.related-posts .slider-container h3.title{
    padding:14px 0 !important;
}
.comment-respond h3.comment-reply-title{
    font-size:19px;
    padding:14px 0 !important;
}

.header-search .fa{
    margin:-8px !important;
}
.navbar-header .et-wishlist-widget a {
    top: 2px !important;
}
.media-heading,
.author-desc,
.vcard{
    text-align:left !important;
}
.page-heading.bc-type-left2 .a-center {text-align: center;}
/*Shop*/
.product-information .cart{
    margin-bottom: 10px;
}
.etheme_widget_brands ul li:last-child,
.widget_layered_nav ul li:last-child{
    border:none;
}

body .product-information table.variations td.label label{
    text-transform:capitalize !important;
}
.product-information-inner .add_to_wishlist{
    font-family:Lora;
    color:#231f20 !important;
}
.product-information-inner .yith-wcwl-add-to-wishlist {
    margin-top:0px !important;
}
.after-shop-loop{
    margin-bottom:4vw !important;
}
.share-post .share-title{
    font-size:19px !important;
    line-height:18px !important;
}
.product-share .share-title{
    color:#969595 !important;
    text-transform:capitalize !important;
}
.single-product .product-information .yith-wcwl-add-to-wishlist a:not(.alt):before, .product-info-wrapper .yith-wcwl-add-to-wishlist a:not(.alt):before, .product-summary-center .yith-wcwl-add-to-wishlist a:not(.alt):before{
    background-color: transparent !important;
}
.filter-wrap{
    box-shadow: 1px 1px 34px #f6f6f6;
    border:none;
    padding-left:15px;
    padding-right:15px;
}
.shop-filters-area .nano-pane{
    right:-13px !important;
}
.nano-slider{
    background:#9f3659 !important;
}
.onsale{
    font-family:Lora;
    background:#231f20 !important;
    color:#fff !important;
}

body .custom-bg-product .mpc-image .mpc-item,
body .custom-bg-product .mpc-image{
    overflow:visible !important;
}
.custom-bg-product:hover img{
       -webkit-transform: scale(1.06);
    -ms-transform: scale(1.06);
    transform: scale(1.06);
    transition: all 0.4s ease-in-out;
}
form.login .button, form.register .button{
        background-color: #9f3659 !important;
}
.woocommerce-cart .page-heading{
    background-image: url(http://8theme.com/demo/xstore/makeup/wp-content/uploads/sites/39/2017/06/cart.jpg) !important;
    background-position:center center;
}
.archive .page-heading{
    background-image: url(http://8theme.com/demo/xstore/makeup/wp-content/uploads/sites/39/2017/06/Shop.jpg) !important;
    background-position:center center;
}
.single-product .page-heading {
    background-image: url(http://8theme.com/demo/xstore/makeup/wp-content/uploads/sites/39/2017/06/Product-page.jpg) !important;
    background-position:center center;
}


.woocommerce-wishlist .page-heading {
    background-image: url(http://8theme.com/demo/xstore/makeup/wp-content/uploads/sites/39/2017/06/wishlist.jpg) !important;
    background-position:center center;
}
.woocommerce-account .page-heading {
    background-image: url(http://8theme.com/demo/xstore/makeup/wp-content/uploads/sites/39/2017/06/Account.jpg) !important;
    background-position:center center;
}
.product-ajax-list li:nth-child(2n+1) {
    clear:right !important;
}
.mobile-menu-wrapper .menu > li:last-child{
    border:none !important;
}

.woocommerce-tabs .active.et-opened a span::after{
    display:none !important;
}
.single-product .tab-content{
    padding-top:0 !important;
}
.woocommerce-tabs li a span{
    text-transform:capitalize;
}
body .product-information-inner a.add_to_wishlist::before{
    background-color:transparent !important;
}
.woocommerce-tabs li a {
    padding:5px 20px !important;
    margin:0 20px;
    font-family:Lora;
    font-weight:400 !important;
}
.woocommerce-tabs li a:hover,
.woocommerce-tabs li.active.et-opened a{
   background:#ffeae5;
}
.single-product .product-information-inner ins span{
    font-size:40px !important;
}
.single-product .product-information-inner del span{
    font-size:26px !important;
}
 .widget_layered_nav .widget-title,
 .widget_price_filter .widget-title{
     line-height:28px !important;
 }
.fixed-product-block .menu-social-icons{
    float:right;
}
.page-numbers  li a,
.page-numbers  li span{
    font-size:12px !important;
    font-family:Lora;
    font-weight:400 !important;
}
.sb-infinite-scroll-load-more a{
    margin-bottom:0 !important;
}
.variations td select{
    margin-bottom:0 !important;
}
.single-product h2.products-title {
    padding-bottom: 65px;
    margin-bottom: 40px;
    background-image: url(http://8theme.com/demo/xstore/makeup/wp-content/uploads/sites/39/2016/05/img-subtitle.png);
    background-repeat: no-repeat;
    background-position: center bottom;
}
.single-product .woocommerce-tabs .vc_row{
    background-image:none !important;
}
.tp-bullet-title{
    display:none;
}
.single-product .carousel-area .products-title span {
    font-size: 40px;
    font-weight: 400;
    text-transform: none;
}
.single-product .carousel-area{
    margin-bottom:5vw;
}
.shop-filters-area .widget-title {
    text-transform:uppercase;
    color:#231f20;
}
.fixed-product-block .fixed-content .menu-social-icons li a:hover {
    color: #9f3659 !important;
}

body .shop-filters-area ul li a{
    text-transform:capitalize !important;
}
.price_slider_amount button.button{
    background:#9f3659;
    border-color:#9f3659;
    font-family:Lora;
}
.open-filters-btn a{
    background:#9f3659 !important;
    color:#fff !important;
}
.product-title,
.product-title a{
    text-transform:uppercase !important;
}
.price .woocommerce-Price-amount{
    color:#9f3659;
    font-size:15px;
}
del .woocommerce-Price-amount{
    color:#969595 !important;
}
/*Content*/

.home .mpc-product__thumb-wrap{
    display:none !important;
}
.product-btn{
    padding:25px 65px !important;
}
.single-product .fixed-content .woocommerce-Price-amount{
    font-size:40px;
    font-family:Lora;
    font-weight:400;
}
.custom-button:hover{
    border:none;
    width:311px;
    cursor:pointer;
    background-color:transparent !important;
}
.meta-post-timeline{
    width: 116px !important;
    height: 89px !important;
    border: none !important;
    background:url(http://8theme.com/demo/xstore/makeup/wp-content/uploads/sites/39/2017/05/pomada.png) !important;
}
.meta-post-timeline .time-day{
    margin-top:22px !important;
    color:#fff !important;
    font-family:Lora;
    margin-top: 26px !important;
}
.shop-full-width .filter-wrap .filter-content{
    border:none !important;
}
.meta-post-timeline .time-mon{
    color:#fff !important;
     font-family:Lora;
    margin-top: -5px !important;
}
body.single-product .single_add_to_cart_button{
    margin-bottom:0 !important;
    line-height:18px;
}
a.add_to_wishlist::before{
    color:#9f3659;
}
.single-post .grid-post-body h2{
    text-align:center;
}
form.login input,
form.register input,
.woocommerce-billing-fields input,
.woocommerce-billing-fields .select2-choice{
    border:1px solid #e6e6e6 !important;
}
.section-home-galery .custom-button{
    padding: 31px 104px !important;
}
.single-product .single_add_to_cart_button:hover,
.custom-button:hover,
.wpcf7-form input[type=submit]:hover{
    background: url(http://8theme.com/demo/xstore/makeup/wp-content/uploads/sites/39/2017/06/black-img.png) no-repeat;
    background-color: transparent !important;
}
.custom-button,
.single-product .single_add_to_cart_button,
.wpcf7-form input[type=submit]{
    line-height: 25px;
    padding: 31px 89px;
    color:#fff !important;
    font-size:13px;
      letter-spacing:2px;
      margin-top:40px;
      font-family:Lora;
      font-weight:400;
      width:311px;
      border:none;
      height:75px;
     background:url(http://8theme.com/demo/xstore/makeup/wp-content/uploads/sites/39/2017/06/pink-img.png) no-repeat;
}
.separatop-block{
    height:1px;
}
.separatop-block .vc_sep_line{
   border-color:#e3e3e3 !important;
}
.single-post .comment-form input[type="email"],
.single-post .comment-form input[type="text"],
.single-post .comment-form input[type="password"],
.single-post .comment-form textarea{
    border-bottom:1px solid #d9afbd;
    background:transparent !important;
    border-top:none !important;
    border-right:none !important;
    border-left:none !important;
}
input[type="text"]:focus, input[type="number"]:focus, input[type="email"]:focus, input[type="search"]:focus, input[type="password"]:focus, input[type="tel"]:focus, textarea:focus, textarea.form-control:focus {
    border-bottom:2px solid #9f3659 !important;
}

/*Blog*/
.blog .page-heading {
    background-image: url(http://8theme.com/demo/xstore/makeup/wp-content/uploads/sites/39/2017/06/blog.jpg);
    background-position:center center;
}
 .post-heading h2 a{
    text-transform:uppercase !important;
    font-size:18px !important;
}
.home .owl-pagination{
    margin-bottom:4vw;
}

.slide-view-timeline2 .meta-post-timeline,
.content-grid2 .meta-post-timeline{
    top:-2px !important;
}
.slider-container .wp-picture img{width:100%;}
.slider-container .blog-hover-animated,
.slider-container .grid-post-body{
    width:100% !important;
}
body .slider-container article.blog-post .read-more,
body .slider-container article.post-grid .read-more {
    display: block !important;
    margin-top: 10px;
    margin-bottom: 10px;
     display: table !important;
     color:#9f3659;
}


body .read-more{
    margin:0 auto;
}

.articles-pagination{
    border:none;
}
.blog-mask .blog-mask-inner a{
    top:-38px !important;
}
.post-categories{
    display:none;
}

/*PreFooter*/
.null-instagram-feed .owl-item li{
    padding:0 !important;
}

/*footer*/
.footer-bottom .textwidget img:hover{
    opacity:1 !important;
}
.footer-copyrights .textwidget,
.footer-copyrights .textwidget a{
    color:#464646;
    font-size:14px;
}
footer .block-follows  .et-follow-buttons a:hover i,
body .copyrights-widget .textwidget a:hover{
    color:#9f3659;
}

footer.footer .mpc-divider-wrap{
    display:none;
}
footer.footer .et-follow-buttons a i{
    color:#8d8c8c;
    margin-top:7px;
}

@media (min-width:482px) and (max-width:769px){
  body .footer.footer .footer-widgets .mpc-icon-column .mpc-icon{
      float:left !important;
      margin-right:5px !important;
  }
  body footer.footer .mpc-icon-column__content-wrap .mpc-icon-column__content .mpc-icon-column__description p{
      margin-top:-12px !important;
  }
  .block-follows{
      width:100%;
      padding-left:15px;
  }
  
}


@media only screen and (max-width:481px){
    .meta-post{
        font-size:15px !important;
    }
    .post-heading {
    margin-bottom: 1.2em !important;
}
body .resp-margin-top{
    margin-top:15px !important;
}
body .block-products .wpb_content_element.responsive-center{
    margin-top:-10px !important;
}
   footer.footer div,
    footer.footer p,
   .responsive-center,
   .block-follows .et-follow-buttons{
       text-align:center !important;
   }
   .woocommerce-breadcrumb{
     margin-bottom:0;
   }
   .woocommerce-variation-add-to-cart .single_add_to_cart_button{
      position:relative;
      left:-10px;
    }
    footer.footer{
        padding-top:40px;
    }
     body .page-heading{
        margin-bottom:50px;
        background-size:cover;
    }
    .single-product .page-heading{
        background-size:contain;
    }
     .block-follows .et-follow-buttons{
        padding-top:15px;
    }
    .wpcf7-form input[type=submit]{
        background-size:contain;
        padding:25px;
    }
    .custom-button{
          padding: 26px 59px;
    background-size: contain;
  }
  .button-see-all .custom-button{
      padding:26px 69px;
  }
  .section-home-galery .custom-button {
    padding: 24px 77px !important;
    background-size: contain;
}
    .responsiv-padding-bottom{
        padding-bottom:30px;
    }
    .responsiv-margin-top{
        margin-top:40px;
    }
    
    footer.footer .mpc-icon{
        margin-bottom:-5px !important;
    }
    body footer.footer  .mpc-icon-column__content .mpc-icon-column__description p{
        margin-bottom:20px !important;
    }
    .after-shop-loop{
        margin-bottom:40px;
    }
}



@media only screen and (min-width: 992px) {
    .after-shop-loop .sb-infinite-scroll-load-more a{
        margin-left:-200px;
    }
    .products-list .content-product {
     display: -webkit-box; 
  display: -moz-box;
  display: -ms-flexbox;
  display: -webkit-flex; 
  display: flex;
  -webkit-box-align: center;
  -moz-box-align: center;
  -ms-flex-align: center;
  -webkit-align-items: center;
  align-items: center;
    }
}

/*Acaunt*/
.woocommerce-account form.login p.form-row{
    float:left;
}
.woocommerce-account form.login .woocommerce-Button{
    float: left;
    width: 39%;
    margin-right: 0;
}
.woocommerce-account form.login .inline{
    margin-top: 7px;
    float: right;
    margin-right: 0px;
}
.woocommerce-account form.login .woocommerce-LostPassword{
    margin-top:7px;
}
