custom_css:
h2.vc_custom_heading {
    text-transform: none;
}

.btn-gradient,
input[type="submit"].btn-gradient{
    border: 2px solid #1cbffa;
    border-image: linear-gradient(90deg, #1cbffa 0%, #58e2b1 100%);
    border-image-slice: 1;
    background: -webkit-linear-gradient(#1cbffa, #58e2b1);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    color:#fff;
}

.text-gradient{
    background: -webkit-linear-gradient(#1cbffa, #58e2b1);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}
.posts-slider article > div .wp-picture{
    display:none;
}
.posts-slider article > div{
    border-top:3px solid #ebebeb;
    padding-top:40px;
}
.posts-slider article h2{
    font-size:24px;
    line-height:34px;
    letter-spacing: -0.25px;
}
.posts-slider article > div:hover{
    border-top: 3px solid #1cbffa;
}
.header-search.act-full-width .search-btn span{
    display:inline-block;
    text-transform:uppercase;
    font-weight:700;
    letter-spacing:1px;
    font-size:13px;
    vertical-align:middle;
    padding-right:20px;
    border-right:1px solid rgba(255,255,255,0.4);
}
.header-search .fa{vertical-align:middle;font-size:24px !important;}
.header-search.act-full-width.search-open .search-btn span{
    display:none;
}
@media only screen and (max-width:1200px){
    .text-gradient{
        background: transparent;
        -webkit-background-clip: text;
        -webkit-text-fill-color: #3bd1d5;
    }
}

.btn-gradient:hover,
input[type="submit"].btn-gradient:hover{
    background: linear-gradient(90deg, #1cbffa 0%, #58e2b1 100%);
    -webkit-text-fill-color: #fff;
}
.menu-wrapper .menu > li > a:after{
    background: linear-gradient(90deg, #1cbffa 0%, #58e2b1 100%);
}
.page-heading .back-history{
    border:2px solid rgba(255,255,255,0.3);
    padding:13px 20px 13px 30px !important;
}
.page-heading .back-history:before{
    left:18px;
}
.sidebar-widget .widget-title{
    font-weight:400;
    font-size:18px;
}

.marketing-price-block .mpc-pricing-column{
    border-top:5px solid #fff;
}

.marketing-price-block .slick-track{
    padding-top:0;
    margin: auto;
}

.marketing-price-block .mpc-button__content{
    border: 2px solid #1cbffa;
    border-image: linear-gradient(90deg, #1cbffa 0%, #58e2b1 100%);
    border-image-slice: 1;
    
}
.marketing-price-block .mpc-button__title,
.btn-gradient .mpc-button__title{
  background: -webkit-linear-gradient(#1cbffa, #58e2b1);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  color:#fff;
}
.marketing-price-block .mpc-button__content:hover .mpc-button__title,
.btn-gradient:hover .mpc-button__title{
    background:transparent;
    -webkit-text-fill-color: #fff;
}

.marketing-price-block:hover .mpc-pricing-column{
    border-top: 5px solid #1cbffa;
    -webkit-box-shadow: 0 15px 25px 0 rgba(1,1,1,.07);
    box-shadow: 0 15px 25px 0 rgba(1,1,1,.07);
}
.marketing-price-block .slick-list{
    overflow: visible;
}
.btn-gradient:hover{
    background: linear-gradient(90deg, #1cbffa 0%, #58e2b1 100%);
}

/* pricing-price */
.mpc-pricing__price{position:relative;}
.mpc-pricing__price:before{
    content: "$";
    font-size: 36px;
    vertical-align: top;
    font-weight: 400;
    position: relative;
    top: 15px;
}
.mpc-pricing__price:after {
    content: "/month";
    vertical-align: down;
    position: relative;
    color: #999999;
    font-size: 12px;
    font-weight: 400;
}


/* Mailchimp - Prefooter */
.prefooter .mc4wp-form .et-mailchimp input[type="email"]{
    height: 50px;
    background-color: rgba(255, 255, 255, 0.2);
    border: 0;
    transition: all 0.2s linear;
}
.prefooter .mc4wp-form .et-mailchimp input[type="submit"]{
    width: 50px;
    height: 100%;
    background-color: rgba(255, 255, 255, 0.15) !important;
    opacity: 1;
}
.prefooter .mc4wp-form .et-mailchimp input[type="email"]:focus{
    background-color: rgba(255, 255, 255, 0.4);
    border: 0;
}
.prefooter p {
    font-weight: 300;
}

/* Footer - Copyright */
.marketing-footer-blog-links li a:hover {
    color: #19b2ff !important;
}
@media only screen and (max-width: 767px) {
    .copyright-footer .et-follow-buttons {
        text-align: center;
    }
    .copyright-footer p {
        text-align: center !important;
        margin-bottom: 10px !important;
    }
    footer.footer{
        text-align: center;
    }
}
