custom_css:
/*Bredcrumbs*/
.template-container .page-heading .col-md-12 {
  display: -webkit-box;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  -webkit-box-orient: vertical;
  -webkit-box-direction: reverse;
  -webkit-flex-direction: column-reverse;
      -ms-flex-direction: column-reverse;
          flex-direction: column-reverse;
}

/*Page Blog (Admin images)*/
.blog .blog-post .post-heading .author-info{
    border-radius:0%;
    border:8px solid #fff;
}
.blog .blog-post .post-heading .author-info img{
    border-radius:0%;
}
.blog .post-heading-inner{
    margin-bottom:5px;
}
.home .post-heading h2{
    font-size:24px;
    font-weight:300;
}
.back-history{
    letter-spacing:normal !important;
}

.footer-product .add_to_wishlist, 
.footer-product .add_to_cart_button, 
.footer-product .show-quickly {
    background:#ccc !important;
}
.footer-product .add_to_wishlist:hover,
.footer-product .add_to_cart_button:hover,
.footer-product .show-quickly:hover{
    background:#1a1a1a !important;
}

.blog article{margin-bottom:120px;}
.post-categories{display:none;}
.single-post .entry-title{
    font-weight:300;
}
/*Onsale*/
.onsale{
    background:#4d4d4d;
}
/*Letter spacing*/
.letter-spacing{
    letter-spacing:2px !important;
}
.letter-spacing-5{
    letter-spacing:5px !important;
}

.letter-spacing-20{
    letter-spacing:20px !important;
}
/* -- Product single -- */

.woocommerce .product-information a.add_to_wishlist.button:before {
    content: "\f08a" !important;
    font-family: FontAwesome !important;
    background-color: transparent !important;
    transition: all 0.2s linear;
    margin-right: 5px;
}
.product-information .yith-wcwl-add-to-wishlist {
    display: block;
    margin-bottom: 0 !important;
}
.product-information .single_add_to_wishlist.button {
    padding-left: 0;
    Font-size: 13px;
}
.product-content .compare {
    margin-bottom: 30px;
}
.single-product .product-information-inner .price ins,
.product-information .price, .product-info-wrapper .price, .product-summary-center .price {
    font-weight: 300;
    font-size: 2.5rem;
}
.single-product .minimal-single-tabs .vc_tta-title-text, .single-product .tabs .tab-title {
    font-weight: 400;
    color: #999999;
}
.minimal-single-tabs .vc_active .vc_tta-title-text, .single-product .tabs .et-opened .tab-title {
    color: #1a1a1a;
}
.single-product .minimal-single-tabs .vc_tta-color-grey.vc_tta-style-classic .vc_tta-tab a:after{
    display: none !important;
}
.single-product .tabs .tab-title  span::after{
    display:none;
}

/*Floats*/
.float-right{
    float:right;
}
.float-left{
    float:left;
}

/* Shop */
.content-product .products-page-cats {
    margin-bottom: 0.26em !important;
}
.product-title .price del span,
.product-title .price ins span{
   font-size:15px !important;
} 
.content-product .product-title a, .content-product .products-page-cats a, .content-product .price, .price ins {
    font-weight: 400 !important;
}
/*Footer*/
footer ul{margin:0;}
footer .menu-element.horizontal li {
    padding: 0 60px 0 0;
    letter-spacing:2px;
}

/*Color footer icons*/
footer .et-follow-buttons.icons-filled a i {
    color: #737373;
}

/*Responsive footer menu li*/
@media only screen and (min-width:992px) and (max-width:1200px){
    footer .menu-element.horizontal li {
      padding: 0 30px 0 0;
    }
}

