custom_css:
::selection{
    background:#f0a455;
}

/*Header*/
.fixed-header .container{
    padding-top: 0.5em !important;
    padding-bottom: 0.5em !important;
}
.menu-wrapper.menu-align-center .menu {
    text-align: right !important;
}
header.header .container-wrapper{
    padding:1.2em 0 !important;
    border-bottom:1px solid #f7f7f7;
}
.fixed-header ul li:last-child a{
    color:#fff !important;
}
.fixed-header .navbar-header{
    display:none;
}
.menu li:last-child a:hover{
    background: #f0a455;
    color:#fff !important;
}
.menu li:last-child a{
    text-align:center !important;
    margin-right:20px;
    background: #434053;
    width: 220px;
    height: 51px;
    border-radius: 60px;
    line-height: 31px !important;
    font-size:14px !important;
    font-weight:500 !important;
    text-transform:uppercase !important;
    margin-left:50px;
    color:#fff !important;
}
.fixed-header{
    border-top:none;
}
.fixed-header .menu-main-container li a{
    color:#aba8b7 !important;
}
.menu-main-container li a:hover,
.fixed-header .menu-main-container li.current-menu-item a,
.menu-main-container li.current-menu-item a {
    color:#434053 !important;
}
body .fixed-header .menu-main-container li.current-menu-item:last-child a{
     color: #fff !important; 
}

.menu-main-container li.current-menu-item a:after,
.menu-main-container li a:hover:after{
    display:none;
}


/*bredcrambs*/
.page-heading {
    margin-bottom: 3.142em;
}
.page-heading h1{
    margin-top:6px;
}
.page-heading .col-md-12.a-center:after{
    margin-top:12px;
    position:absolute;
    width:100%;
    left:0;
    right:0;
    content:url(http://8theme.com/demo/xstore/mobile/wp-content/uploads/sites/52/2017/10/title-img.png);
}

/*product*/
.single-product-booking .product-side-information-inner .product-share {
    padding: 0 !important;
   
}
.single-product-booking .product-side-information-inner .product-share a{
     color:#b6b8b8;
}
.tagged_as a{
    text-transform:capitalize;
    letter-spacing:1px;
    color:#b6b8b8 !important;
}
.tagged_as{
    font-weight:500;
    margin-top:15px;
}
.single-product-booking .product-side-information-inner .yith-wcwl-add-to-wishlist a:not(.alt),
.product-content .compare{
    font-size: 16px;
    font-weight: 400;
    color:#434053;
}
.single-product-booking .product-side-information-inner .price {
    margin-bottom: 40px;
}
.quantity.buttons_added .minus:hover:before, 
.quantity.buttons_added .plus:hover:before {
    color: #c968cf;
}
.minus {
    margin-right: -6px;
}
.plus {
    margin-left: -6px;
}
.minus, .plus, .minus + .input-text {
    background: transparent !important;
    border: 1px solid #d1d1d1 !important;
    color: #4d4d4d !important;
}
.single-product .single-product-booking .quantity {
    margin-right: 0;
    margin-bottom: 24px;
}
.product-side-information-inner .price * {
    font-size: 55px !important;
}
.single-product-booking .product-side-information-inner .product-price-title{
    display:none;
}
.single-product .product-information-inner {
    text-align:center;
}
.product-information-inner p{
    margin-bottom:5px;
}
.product-information .product_title{
    font-size: 21px;
    font-weight: 700;
    text-transform: capitalize;
}
.single-product .product-side-information-inner > div {
    background: transparent;
    border: 1px solid #ededed;
    border-radius:30px;
    padding: 50px 20px 30px 20px !important;
}


/*Blog*/
.post-categories {
    display: none;
}
.single-post .post-categories {
    display: none;
}
.single-tags a {
    background-color: #f2f2f2;
    color: #b6b8b8;
}
.price-span{
    font-size:21px;
}
.price--product{
    font-size:41px;
    font-weight:700;
    padding-top:10px;
    width: 40%;
    margin: 0 auto;
}
.text-fradient{
    width:54px !important;
}
.menu-social-icons  li a:hover i,
.price--product,
.advantages,
.et-follow-buttons a:hover i,
.product-side-information-inner .price *,
.text-fradient{
     background:  linear-gradient(135deg, rgba(202,104,207,1) 0%, rgba(233,153,67,1) 80%, rgba(233,153,67,1) 100%);
     -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}
.advantages{
    font-size:65px;
    opacity:.2;
    position: relative;
    top: 13px;
}
.mpc-tooltip.mpc-wrap-content {
     white-space: nowrap !important;
}

.gradient,
.single-tags a:hover{
     background:  linear-gradient(135deg, rgba(202,104,207,1) 0%, rgba(233,153,67,1) 80%, rgba(233,153,67,1) 100%);
}
.posts-nav-btn.prev-post .post-title,
.posts-nav-btn.next-post .post-title{
    background:#fff;
    border:1px  solid #f0a455;
    color:#434053;
    line-height:18px;
}
.posts-nav-btn .button:before{
    color:#fff;
}
.posts-nav-btn .button,
.posts-nav-btn.prev-post:hover .button{
    background: #cd5188 !important;
}
.share-post .menu-social-icons  li a{
    color:#cccccc;
    padding-top:3px;
}
.menu-social-icons i {
    padding-top: 5px;
}
.media-body p .author-link.url {
    color: #434053;
    font-weight: 700;
    font-size: 16px;
}
 .comment-form .form-group{
    margin-bottom:25px !important;
}
.comment-reply-title span,
.share-post .share-title{
    font-size: 18px;
    color: #434053;
}
.blog .et_load-posts.button-loading span:hover,
.comment-respond .comment-form .form-submit .submit:hover{
    background:#f0a455 !important;
    border-color:#f9f0fa !important;
}
.et_load-posts .btn a {
    font-size: 14px;
    color: #fff;
}
.blog .et_load-posts.button-loading,
.comment-respond .comment-form .form-submit{
    margin-bottom:100px;
}
 .single-product .add_to_cart_button:hover{
    background: #cd5188 !important;
    border-color:#f9f0fa !important;
}
 .single-product .add_to_cart_button{
    background: #f0a455 !important;
    margin-bottom:6px;
}
.blog .et_load-posts.button-loading span,
.comment-respond .comment-form .form-submit .submit,
.single-product .add_to_cart_button{
    background: #cd5188;
    border: 10px solid #f9f0fa;
    width: 220px;
    height: 74px;
    border-radius: 60px;
    line-height: 53px;
    font-size:14px !important;
    font-weight:500 !important;
}
article.post-grid {
    margin-bottom: 70px;
}
article.post-grid .wp-picture{
    margin-bottom:30px !important;
}
article.blog-post.post-grid .grid-post-body:hover{
     border-color:#e5e5e5;
}
.read-more{
    margin:0 auto;
}
article.blog-post.post-grid *{
    text-align:center !important;
}
article.blog-post.post-grid .meta-post{
    font-size:16px;
}
article.blog-post.post-grid .grid-post-body{
    padding:50px 10px 40px 10px;
    border:1px solid #f2f2f2;
    border-radius:35px;
}


article.blog-post.post-grid .post-heading h2,
article.blog-post.post-grid .post-heading{
    margin-bottom:20px;
}
article.blog-post.post-grid .post-heading h2 a{
    font-size:25px;
    letter-spacing:0;
}

/*Content*/

.pulse-img{
    animation-name: pulse;
    -webkit-animation-name: pulse;  
 
    animation-duration: 1.5s;   
    -webkit-animation-duration: 1.5s;
 
    animation-iteration-count: infinite;
    -webkit-animation-iteration-count: infinite;
}


body .subscribe-section{
    background-position:bottom center !important;
}
.about-us-block .mpc-list__ul{
    margin-bottom:0 !important;
    padding-left:0 !important;
}
 .price-block:hover{
    border-color:#e5e5e5 !important;
}
.container.content-page{
    padding-bottom:0 !important;
}
.medium-500{
    font-weight:500;
}
body .capitalize{
    text-transform:capitalize;
}
body .mc4wp-form input[type=email] {
    height: 50px !important;
    border: 1px solid #f3dee8;
    border-radius: 40px;
    padding: 0 25px;
    font-size:16px;
}
body .form.mc4wp-form input[type="submit"]:hover{
    background:#f0a455 !important;
}
body .form.mc4wp-form input[type="submit"] {
    width:164px;
    color: #fff;
    height: 50px;
    border: none !important;
    border-radius:40px;
    position: absolute;
    top: 23px; 
    bottom: 0;
    right: 0;
    background: #434053 !important;
    font-size:14px;
    font-weight:500;
}

/*footer*/
footer.footer ul.menu li:last-child{
    display:none;
}
footer.footer ul.menu li a:hover{
    color:#434053 !important;
}
body footer.footer .et-follow-buttons.buttons-size-normal a {
    font-size: 18px !important;
    padding: 0px 10px;
}
footer.footer{
    border-top:1px solid #f7f7f7;
}
footer.footer .menu-align-center li a{
    text-transform:capitalize;
    font-weight:500;
    font-size:16px;
}
footer.footer .menu{
    margin-bottom:0;
}
.text-copiright {
    font-size: 16px;
    color: #b6b8b8;
}


@media screen and (max-width: 768px) {
    .header.main-header.header-bg-block{
        height:66px !important;
    }
    .responsive-center,
    .responsive-center *{
        text-align:center !important;
    }
    .mobile-menu-wrapper  ul li:last-child,
    .d-none{
        display:none !important;
    }
    .price--product{
        width:100% !important;
    }
    .price-block .mpc-button__title.mpc-transition{
      padding-top: 16px !important;
      padding-right: 35px !important;
      padding-bottom: 16px !important;
      padding-left: 34px !important;
    }
    header.header .container-wrapper {
    padding: 0.6em 0  !important;
     border-bottom: none; 
    }
    .fixed-header .container {
      padding-top: 0.5em;
      padding-bottom: 0.5em;
    }
    .header-logo img {
    max-width: 96px !important;
    }
    footer.footer *{
        text-align:center !important;
    }
    footer.footer .et-follow-buttons,
    footer.footer .menu,
    footer.footer img{
        margin-bottom:30px !important;
    }
    footer.footer .menu-element.horizontal li {
       padding: 5px 10px;
    }
    
    .about-us-block {
        margin-bottom:120px;
    }
    .left-block-about-us .vc_column-inner,
    .about-us-block .vc_column-inner{
        margin-left:0 !important;
    }
    
    body .phone-slider-block{
        margin-bottom:80px !important;
    }
    .mpc-lightbox.mpc-transition.mpc-magnific-popup {
      top: 12% !important;
      left: 36% !important;
      width: 31%;
    }
    body .price-section{
        margin-bottom:60px !important;
    }
    .price-section .text-center:first-child .price-block{
        margin-right:30px !important;
    }
    .price-section .text-center:last-child .price-block{
        margin-left:0px !important;
    }
    .team-block{
        margin-top:80px;
    }
    .team-block .wpb_column.vc_column_container{
        margin-bottom:50px !important;
    }
    .single-tags a{
        margin-bottom:4px;
    }
    .team-left-block .wpb_column{
        margin-bottom:30px !important;
    }
   body  .subscribe-section{
        padding-top: 150px !important;
        background-size: cover !important;
        padding-bottom:0 !important;
    }
    body .form.mc4wp-form input[type="submit"] {
         width: 118px !important;
        font-size: 12px;
        right:16px !important;
    }
    .page-heading{
        padding-bottom:90px !important
    }
    .page-heading h1{
        font-size: 40px !important;
    }
    .text-fradient {
       width: 100% !important;
     }
    
    
    
    
}


@media screen and (max-width: 488px) {
    .fixed-header {
    box-shadow:none !important;
    }
    body h2.capitalize{
        font-size:37px !important;
        line-height:42px !important;
    }
 .price-block .mpc-button__title.mpc-transition{
      padding-top: 18px !important;
      padding-right: 54px !important;
      padding-bottom: 18px !important;
      padding-left: 54px !important;
    }
}
