custom_css:
::selection{
    background:#bf3937;
}

/*Header*/
.header-wrapper.header-double-menu .container-wrapper {
    border-bottom: none;
}
li.current-menu-item a{
    color:#fcd20c !important;
}
header .menu-main-container li a:hover{
    color:#fcd20c !important;
}
header .menu-main-container li a:after{
    display:none !important;
}
.single-product .product-images .onsale {
    z-index: 101;
    border-radius: 100%;
}
.fixed-header{
 border-top:none;    
}
.page-heading{
    margin-bottom:80px;
}
.product-view-mask3 .footer-product .yith-wcwl-add-button a.add_to_wishlist:hover,
.product-view-mask3 .footer-product .button:hover,
.product-view-mask3 .footer-product .show-quickly:hover{
    background:#bf3937 !important;
}
/*Breadcrumbs*/
.woocommerce-breadcrumb + h1:after,
.page-heading h1.title span:after,
.single-product .products-title span:after,
.heading-blocks:after{
    content:url(http://8theme.com/demo/xstore/pizza/wp-content/uploads/sites/47/2017/08/separator.png);
    margin-left:35px;
    position: relative;
    bottom: 12px;
}
.woocommerce-breadcrumb + h1:before,
.heading-blocks:before,
.single-product .products-title span:before,
.page-heading h1.title span:before{
     content:url(http://8theme.com/demo/xstore/pizza/wp-content/uploads/sites/47/2017/08/separator.png);
    margin-right:35px;
    position: relative;
    bottom: 12px;
}
.menu-wrapper .menu > li > a:after {
    background-color: #fcd20c !important;
}

body .content-product .product-title a,
.product-information-inner .products-page-cats a{
    text-transform:uppercase;
    font-family:Oswald;
    font-weight:700;
    font-size:22px;
    color:#1c1a19;
}
 body .price{
    color:#fcd20c !important;
}
.single-product .star-rating {
    margin-bottom:12px;
}
.single-product .product-information-inner .woocommerce-Price-amount{
    letter-spacing:2px;
}
.product-information .woocommerce-product-rating{
    margin-bottom: 0.63em;
}
.quantity.buttons_added{
    background: #fff;
    border: 1px solid #ccc;
    border-radius: 30px;
     padding: 4px 8px;

}
.input-text.qty.text{
    font-size:20px !important;
}
.minus:hover:before,
.plus:hover:before{
    color:#bf3937 !important;
}

.minus,
.plus,
.input-text.qty.text{
    background:transparent !important;
    border:none;
    
}
.cart-widget-subtotal .small-h {
    font-family: Oswald;
}
.single-product .variations + .qty-span{
    display:none;
}
.product-information table.variations td.label label{
    font-family:Oswald;
    font-size:12px;
    letter-spacing:1px;
}
.product-information .products-page-cats {
    margin: -0.65em 0 0.25em;
}
.product-information-inner h4.title{
    display:none !important;
}
.page-heading.bc-size-large .title {
    max-width: 100% !important;
}
.single-product .products-title{
    margin-top:80px;
}
.single-product .product-information-inner{
    margin-top:120px;
}
.single-post article  h2.entry-title{
    text-transform:initial !important;
}
.single-product .single_add_to_cart_button:hover{
    background:#fcd20c !important;
}
.single_add_to_cart_button {
    background:#bf3937 !important;
    border-radius:30px;
    font-family:Oswald;
    font-size:16px;
    font-weight:700;
    border:none;
    min-width:150px;
    margin-top:-2px;
}
.single-product .swiper-container.carousel-area{
    padding-bottom:50px;
    margin-bottom:3vw;
}
.woocommerce-Price-amount{
    color:#fcd20c !important;
    font-family:Oswald;
}

.single-product .product_meta .sku_wrapper {
    display:none;
}
.product-details .price *{
    font-size:14px;
    color:#fcd20c;
      font-family:Oswald;
    font-weight:500;
}
.header-custom a {
    font-family: Oswald;
    font-weight: 500;
    vertical-align: middle !important;
    display: table-cell !important;
    padding: 1.8em 1.07em !important;
    font-size:16px;
}
/*Content*/
input[type="submit"]{
    padding: 0 32px;
    letter-spacing: 1px;
    font-size: 18px;
    font-family: Oswald;
    background: #fcd20c;
    border-color: #fcd20c;
    border-radius: 30px;
    color: #fff;
    height: 48px;
}
input[type="submit"]:hover{
    background:#bf3937 !important;
    border-color:#bf3937 !important;
}
form.login .button:hover, form.register .button:hover {
    background-color: #bf3937 !important;
    border-color: #bf3937 !important;
}
form.login .button, form.register .button {
    background-color: #fcd20c;
    border-color: #fcd20c;
}

.fa-quote-right{
    color: #fddb3d;
    right: -10px;
    position: relative;
    top: -3px;
}
.fa-quote-left{
    color: #fddb3d;
    left: -10px;
    position: relative;
    top: -3px;
}
.home .shadow-block {
    background:#fff;
}

.shadow-block .vc_column-inner {
    padding:45px 0;
}
body .hover-block:hover .shadow-block,
.home .hover-block:hover .vc_column-inner .shadow-block{
    background:#fcd20c !important;
}
.hover-block:hover .shadow-block *{
    color:#fff !important;
}
.shadow-block{
    box-shadow:0 7px 30px #e9e9e9;
}
.delivey-block p{
    font-family:Oswald;
    text-transform:uppercase;
    letter-spacing:3px;
    font-size:12px;
}

del .woocommerce-Price-amount{
    color:#8a8a8a !important;
}

.delivey-block p:last-child{
    font-family:Oswald;
   font-size:16px;
    letter-spacing:3px;
    font-weight:bold;
}
body header .badge-number,
.header-color-dark .shopping-container.ico-design-2 .cart-bag .badge-number{
    background:#bf3937 !important;
}
.wc-proceed-to-checkout,
.button.btn-checkout,
.btn-view-cart{
    font-family:Oswald;
}
body .vc_custom_heading.heading-blocks,
.products-title{
    text-transform:initial !important;
    letter-spacing:1px;
    font-family:Qwigley;
    font-weight:400;
}
.products-title{
    color:#bf3937;
}
.letter-5{
    letter-spacing:5px;
}
.letter-1{
    letter-spacing:1px;
}

/*blog*/
.posts-nav-btn .post-info{
    padding:5px;
}
.posts-nav-btn .button {
    width: 80px;
    height: 80px;
    line-height: 85px;
}
.posts-nav-btn .post-info .post-title{
    font-family:Oswald;
}
.svg-wrapper a{
    top:-41px !important;
}
.blog-mask-inner a:hover{
    background:#bf3937 !important;
}
body div.blog-mask::before{
    background:#fff !important;
}
.blog-mask-inner a{
    background:#fcd20c !important;
    border:none !important;
    border-radius:30px;
    font-family:Oswald;
    font-size:18px;
}
.menu-social-icons  li a:hover{
    color:#bf3937 !important;
}
.share-post .share-title,
.comment-reply-title{
    text-transform:uppercase !important;
}
.articles-pagination{
    margin-bottom:5vw;
}
body .page-numbers.current,
body .page-numbers:hover{
    background:#bf3937 !important;
    border-color:#bf3937 !important;
}
.header-color-dark .et-wishlist-widget .wishlist-count{
    background:#bf3937 !important;
}

.single-product .products-title span {
    text-transform: initial;
    font-size: 100px;
    font-weight: 400;
}
.page-numbers{
    border-radius:30px;
}
.wp-picture .post-categories{
    background:#fff !important;
    font-family:Oswald;
    letter-spacing:1px;
}
.blog-post .post-categories a{
    color:#fcd20c !important;
}
.onsale{
    font-family:Oswald;
    font-weight:700;
}
body .sidebar .recent-posts-widget .post-widget-item h4 a{
    font-family: Oswald; 
    text-transform: initial;
    font-weight: 500;
    font-style: normal;
    font-size:16px;
    color: #1c1a19;
}
.post-heading h2.entry-title a{
    font-family: Oswald; 
    text-transform: initial;
    font-weight: 500;
    font-style: normal;
    font-size:28px;
    color: #1c1a19;
}
 .blog .read-more{
    font-family: Oswald; 
    font-size:16px;
    color: #1c1a19;
    letter-spacing:1px;
    font-weight:700;
}
.blog .read-more:before{
    background:#1c1a19 !important;
}
.post-comments-count span,
.views-count{
    color:#c7c7c7 !important;
}
.views-count:before{
    color:#fcd20c !important;
}
.widget-title span{
    font-family: Oswald;
    color: #1c1a19 !important;
     font-weight: 700;
     font-size:16px;
     text-transform:uppercase;
}

/*footer*/
footer.footer .text-border{
    border-left:2px solid #e5e5e5;
    border-right:2px solid #e5e5e5;
    width:85%;
    margin:0 auto;
}

footer.footer .et-follow-buttons{
    margin-bottom:20px;
}
footer.footer  .block-contact-footer p{
     font-family: Oswald;
    color: #1c1a19 !important;
     font-weight: 700;
     font-size:16px;
}
footer.footer ul li a:hover{
    color:#fcd20c !important;
}
footer.footer ul li a {
    font-family: Oswald;
    color: #1c1a19 !important;
    font-size:15px;
    font-weight: 500;
    letter-spacing:1px;
}


@media only screen and (min-width: 1200px) {
  .content-product .product-content-image img {
    min-width: auto;
  }
}
@media only screen and (min-width: 992px){
  .header-wrapper.header-double-menu .navbar-header {
    top: 45%;
  }
  
}
@media only screen and (max-width: 992px) {
    div.mpc-hotspot.mpc-transition.mpc-tooltip-wrap{
        opacity:0 !important;
    }
}
@media only screen and (max-width: 768px) {
    .mpc-product__thumb.mpc-effect--target{
        background-size:contain !important;
    }
     .heading-blocks{
       font-size:70px !important;
   }
}
@media only screen and (max-width: 480px) {
    body .wpb_revslider_element.wpb_content_element{
        margin-bottom:-40px !important;
    }
  
    body .first-section-about-us {
            background-size: contain !important;
            margin-bottom:0 !important;
           min-height:200px !important;
    }
    .fixed-header .header-logo img,
    .header-wrapper .header-logo img{
    max-width: 60px !important;
    }
   .single-post .share-post .menu-social-icons {
      margin-top: 5px;
    }
    .single-product .products-title span {
       font-size: 70px;
    }
    body .button-blok{
        margin-top:130px !important;
    }
    .single-tags a{
        margin-bottom:5px;
        
    }
    .et-loader-img {
       width: 50% !important;
       top: 20%;
    }
    .vc_separator.wpb_content_element.vc_separator_align_center{
        width:100% !important;
        margin-bottom:40px !important;
    }
    .vc_gitem-zone {
        overflow: visible !important; 
         height: 150px !important;
     }
    .vc_gitem-zone-mini .vc_gitem_row .vc_gitem-col {
       padding: 0px 10px;
      }
    .vc_gitem-woocommerce.vc_gitem-woocommerce-product-price{
        margin-top:-10px;
    }
    .menu-section > .vc_row > .wpb_column > .vc_column-inner{
      padding-right: 15px !important;
      padding-left: 15px !important;
    }
    .vc_sep_width_90 {
    width: 100% !important;
     }
    .menu-section .vc_gitem-post-data-source-post_title > div {
    text-align: center !important;
    }
    body .section-menu{
        margin-right:0 !important;
        text-align:center !important;
    }
    body .section-popular-pizza{
      padding-top: 40px !important;
      padding-bottom: 140px !important;
    }
     body .section-team{
       padding-top: 40px !important;
       padding-bottom: 40px !important;
     }
    .page-heading {
      margin-bottom: 0px !important;
     }
    
   .woocommerce-breadcrumb + h1:before,
   .heading-blocks:before,
   .single-product .products-title span:before,
   .page-heading h1.title span:before{
       display:none;
    }
    .page-heading .title span{
        font-size:80px !important;
    }
    .page-heading{
       padding-top: 2em !important;
       padding-bottom: 6em !important;
    }
    .woocommerce-breadcrumb + h1:after,
    .heading-blocks:after,
    .single-product .products-title span:after,
    .page-heading h1.title span:after{
      display:none;
     }
    .block-banner .vc_column-inner{
        width:90% !important;
        margin:0 auto 50px auto !important
    }
    .hidden-block{
        display:none !important;
    }
    .header-wrapper header > .container .container-wrapper {
    padding-top: 1.14em !important;
    padding-bottom: 1.14em !important;
     }
    .menu-section{
        margin-top:60px;
    }
    .first-heading{
        margin-top:30px;
    }
    body .menu-section,
    body .section-team,
    body .block-banner,
    body .maps-section{
        margin-bottom:100px !important;
    }
    footer.footer .text-border{
        border:none !important;
    }
    footer.footer *{
       text-align:center !important;
    }
    .hover-block{
       margin-bottom:50px;
    }
   .responsive-banner .vc_column-inner{
       margin-top:0px !important;
       margin-bottom:50px;
   }
   .breadcrumbs-type-disable.et-header-not-overlap:not(.home) .content-page {
    margin-top: 0px !important;
   }
  
   .meta-post {
    font-size: 1.1rem;
    }
}
