custom_css:
::selection{
    background:#b094ab;
}

.single-post .post-header .post-heading {
    text-align: center;
    margin-top: 60px;
}
.single-post .post-header .post-heading h2{
    text-transform: none;
    font-size: 55px;    
    font-weight: 400;
}

/*header*/
.header-wrapper header > .container .container-wrapper{
    padding-top: 1.5em;
    padding-bottom: 1.5em;
}
.fixed-header .container{
    padding: 16px 0;
}
.fixed-header .container ul li a{
    font-size:16px !important;
}
.fixed-header .header-logo img {
     max-width: 130px;
}
.header-color-dark .menu-wrapper .menu > li:hover  a:after,
.header-color-dark .menu-wrapper .menu > li.current-menu-item  a:after,
.fixed-header li.current-menu-item a:after,
.fixed-header li:hover a:after{
    display:none;
}

header .menu-wrapper .menu > li > a {
    padding: 0.77em 2em;
}

header ul li.current-menu-item a,
.fixed-header li:hover a,
header ul li a:hover,
footer.footer ul li a:hover{
    color:#b094ab !important;
}
.header-wrapper.header-double-menu .container-wrapper {
    border-bottom: none;
}
.fixed-header{
    border-top:none;
}


/*Content*/

.shadow {
    box-shadow:0 0 15px #f3f3f3;
}
.block-team .wpb_column.vc_column_container .vc_column-inner:hover .wpb_single_image {
    box-shadow:0 0 15px #f3f3f3;
    border-color:#f5f5f5 !important;
}
.widget_calendar caption {
    color: #b094ab;
    text-transform: capitalize;
    padding-bottom: 10px;
    font-size: 26px;
    font-weight: 500;
}
.widget_calendar tbody td#today {
    background-color: #b094ab;
    color: #fff;
    font-weight:400;
}
.calendar_wrap *{
    border:none;
}
.calendar_wrap tbody{
    font-size:20px;
}
.calendar_wrap  thead,
.calendar_wrap tfoot{
    display:none;
}
.letter-1{
    letter-spacing:2px;
}
.uppercasse{
    text-transform:uppercase;
}

.capitalize,
.capitalize h3{
    text-transform:initial !important;
}
.block-team .wpb_column.vc_column_container .vc_column-inner .wpb_single_image,
.about_us-block .vc_column-inner .vc_single_image-wrapper {
    overflow:hidden;
}
.recipe-block .vc_column-inner img,
.section-mask .vc_column-inner img,
.block-team .wpb_column.vc_column_container .vc_column-inner img,
.about_us-block img{
    transition: all 0.4s ease-in-out;
}
.recipe-block .vc_column-inner:hover img,
.section-mask .vc_column-inner:hover img,
.about_us-block:hover img,
.block-team .wpb_column.vc_column_container .vc_column-inner:hover img{
    -webkit-transform: scale(1.05);
    -ms-transform: scale(1.05);
    transform: scale(1.05);
    transition: all 0.4s ease-in-out;
}



/*blog*/
.post-comments-count:before{
    color:#b094ab;
}
.swiper-entry .swiper-custom-left,
.swiper-entry .swiper-custom-right {
    top: 55%;
}
.single-post .read-more{
    margin:20px auto;
}
.comment-respond{
    display:none;
}
.single-post .share-post .share-title{
    color: #b094ab;
    font-family: Dosis;
    font-size: 20px;
}
.single-post .menu-social-icons {
    margin-top:6px;
}
.swiper-container.posts-slider h3 span{
    font-size:75px;
}
.share-post.swiper-class-blog{
    margin-bottom:60px;
}
.swiper-container.posts-slider h3,
.share-post.swiper-class-blog{
    border:none;
}
.swiper-container.posts-slider {
    text-align:center;
}
.single-post .share-post.swiper-class-blog .menu-social-icons li a:hover{
    color:#b094ab;
}
.single-post .share-post.swiper-class-blog .menu-social-icons li a{
    color:#d6d6d6;
}
.post-categories{
    display:none;
}
.blog article{
    margin-bottom:60px !important;
}
.blog article > div:hover,
.home article > div:hover,
.related-posts article > div:hover{
    box-shadow: 0px 0px 20px #f4f4f4;
    border-color:#f9f9f9;
}
.blog article .content-article,
.home article .content-article,
.related-posts article .content-article{
    padding: 0 50px 20px 50px;
    
}
.swiper-container-autoheight .swiper-slide {
    padding-bottom:20px;
}
.related-posts .posts-slider article h2 {
    line-height: 1;
}
article .post-heading h2 {
    margin-bottom: 0.39em !important;
}
article .post-heading{
    padding:0 15px;
    margin-bottom: 1em !important;
}
article .post-heading .meta-post{
    font-size:16px !important;
}
article h2{
    font-size:30px !important;
}
article h2 a{
    color:#2a2424 !important;
}
.blog article > div,
.home article > div,
.related-posts article > div{
    border:1px solid #ebebeb;
}
.home table th:last-child,
.home  table td:last-child {
     padding-right:0.71em !important;
}
.home table th:first-child,
.home  table td:first-child {
     padding-left:0.71em !important;
}
.meta-post-timeline .time-day{
    font-family:'Arvo';
    margin-top:10px !important;
    font-size:32px !important;
}
.meta-post-timeline .time-mon{
    font-family:'Arvo';
    font-size:16px !important;
}
.meta-post-timeline{
    background:#b094ab !important;
    width:76px !important;
    height:76px !important;
    top:-12px !important;
}
.meta-post-timeline *{
    color:#fff !important;
}
article:hover .meta-post-timeline {
    background: #575558!important;
    border-color: #575558 !important;
}
.et_load-posts.button-loading .btn{
    border: 2px solid #b094ab;
    background: transparent;
    width: 196px;
    height: 45px;
    font-size: 14px;
    line-height: 40px;
}

.et_load-posts.button-loading .btn:hover{
    background:#b094ab;
}

.et_load-posts.button-loading .btn:hover a{
    color:#fff;
}

.et_load-posts.button-loading .btn a{
    color:#b094ab;
    letter-spacing:2px;
}



/*footer*/
footer.footer .menu-mobile-menu-container ul li a{
    color:#575558;
    font-size:18px;
    font-weight:400;
}

footer.footer .menu-element.horizontal li {
    padding: 0 60px 0 0;
}
footer.footer .mpc-textblock.mpc-inited p a{
    font-weight:600;
    color:#b094ab;
}


@media screen and (min-width: 1800px) {
    .peaple-say > .vc_column-inner{
      margin-left:-50px !important;
    }
}

@media screen and (min-width: 2000px) {
    .peaple-say > .vc_column-inner{
      margin-left:-75px !important;
    }
}

@media screen and (max-width: 780px) {
  .fixed-header .header-logo img {
     max-width: 106px !important;
   }
    .responsive-size{
        font-size:54px !important;
        line-height:60px !important;
    }
    body .section-mask{
        padding-top:90px !important;
    }
    body .banner-home{
        margin-bottom:90px !important;
    }
    .fixed-header {
      box-shadow: none !important; 
      border-bottom:1px solid #f9f9f9 !important;
     }
    footer.footer ul.menu li a{
        font-size:16px !important;
    } 
    .peaple-say > .vc_column-inner{
    margin-left:0 !important;
    }
   .header-wrapper header > .container .container-wrapper {
      padding-top: .8em;
      padding-bottom: 0.4em;
    }
    .fixed-header .container {
      padding: 10px 0;
    }
    .wpcf7-form-control.wpcf7-submit{
        margin-bottom:65px;
    }
    .wpcf7-form p{
        text-align:center;
    }
    body .contact-info-block .vc_column-inner{
        margin-left:0 !important;
        margin-bottom:80px;
        background:none !important;
    }
   body .et-follow-buttons.buttons-size-normal,
   body .about-us-1{
        margin-left:0 !important;
    }
    body .responsive-center *{
        text-align:center !important;
    }
    
    footer.footer .menu-element.horizontal li {
       padding: 5px 15px;
    }
    .recept-2{
        margin-bottom:350px !important;
    }
    body .about-us-2{
        margin-right:0 !important;
    }
    .resipe-margin-bottom{
        margin-bottom:100px !important;
    }
    body .recipe-block .vc_row.wpb_row.vc_inner{
        margin-bottom:180px;
    }
    body .recipe-block  .recipe_responsive .vc_column-inner{
        margin-top:0 !important;
    }
    
    body .recipe-block .vc_column-inner{
        margin-left:0 !important;
        margin-right:0 !important;
    }
    body .smile-block-responsive{
        margin-top:50px !important;
    }
    body .contacts-block,
    body .section-mask,
    body .section-about-us,
    body .review{
        background:none !important;
    }
    .block-team .wpb_column.vc_column_container,
    .right-block__img .mpc-button{
        margin-bottom:50px;
    }
    .et-header-overlap .page-heading, .et-header-overlap .page-heading.bc-size-small {
      padding-top: 10em;
      padding-bottom: 5em;
     }
    body .smile-block-about-us{
      margin-left: 0px !important; 
      font-size: 55px !important;
      line-height: 65px !important;
    }
    .banner-home h2{
        font-size:44px !important;
        margin-bottom:40px !important;
    }
}
