custom_css:
::selection{
    background:#e6bf4e;
}

.single-post .post-categories,
.wp-picture .post-categories{
    display: none !important;
}
.single-post .post-heading {
    text-align: center;
}





/*header*/

.fixed-enabled .header-logo img {
    max-width: 140px !important;
}
.fixed-header > .container{
    padding-top: .8em;
    padding-bottom: .6em;
}
.fixed-header .navbar-header{
    display:none !important;
}
.menu-wrapper.menu-align-center .menu {
    text-align: right !important;
}
body .fixed-header{
    background:transparent !important;
    border-top:none;
     box-shadow:none;
}
li.menu-item a:hover{
    color:#ccc !important;
}
li.current-menu-item a{
    color:#ccc !important;
}
li.current-menu-item a:after{
    background-color:#fbda7b !important;
}
li.menu-item a:hover:after{
    background-color:#fbda7b !important;
}


/*bredcrumbs*/
.page-heading h1.title{
    margin-left:-110px !important;
}
.back-history{
    letter-spacing: 1px !important;
}
.back-history:hover{
    color:#fbda7b !important;
}

.page-heading h1 span:before,
.single-product .page-heading h1:before{
    content: url(http://8theme.com/demo/xstore/typography/wp-content/uploads/sites/50/2017/09/bredckrumbs-img.jpg);
    margin-left: 20px;
    position: relative;
    bottom: 70px;
    left:40px;
}

.head-after{
    text-transform:capitalize !important;
}

/*Blog*/
.single-post .page-heading h1 span:before, .single-post .page-heading h1:before {
    bottom: 46px;
}
.single-post .page-heading span{
    font-size:34px !important;
}
.comment-form .submit {
    margin-top: 20px;
      font-weight: 800;
    height: 50px !important;
     box-shadow: 5px 5px 0px 0px #fbda7b !important;
     width:164px;
     margin-bottom:6vw;
}
.share-post .menu-social-icons  a{
    color:#b8b8b8;
}
.share-post .menu-social-icons {
    margin-top:6px;
}
.share-post .share-title,
.comment-reply-title{
    font-size:14px;
    text-transform:uppercase !important;
    font-weight:400;
}
article.blog-post .wp-picture.blog-hover-animated .btn, article.post-grid .wp-picture.blog-hover-animated .btn {
    top: -38px;
}
article .wp-picture.blog-hover-animated {
    box-shadow: 10px -10px 0px 0px #fbda7b;
}
.post-heading{
    margin-bottom:5px;
}

.post-heading h2 {
    margin-bottom:10px;
}
.post-heading h2 *{
    font-size:30px;
    letter-spacing:0 !important;
}
.meta-post {
    font-size:20px;
    font-family: "Advent Pro";
}
.meta-post *{
  color:#fbda7b !important;  
}
.portfolio-filters{
    display:none;
}
.blog article{
    padding-top:30px;
}
.blog .hfeed.et_blog-ajax.row.blog-masonry{
    margin-bottom:4vw;
}

/*shop*/
.product-share li a{
    color:#bfbfbf !important;
}
.product-share li a:hover{
    color:#e6bf4e !important;
}
.single-product-booking .product-side-information-inner .price {
    margin-bottom: 40px;
}
.single-product .single-product-booking .quantity {
    margin-right: 0;
    margin-bottom: 30px;
}
.single-product .yith-wcwl-add-to-wishlist {
    margin-top: 25px;
}
.tagged_as{
    margin-top:20px !important;
}
.tagged_as a {
    text-transform:capitalize;
    color:#c4c4c4 !important;
}
.tagged_as a:hover {
    color:#e6bf4e !important;
}
.single-product-booking .product-side-information-inner .yith-wcwl-add-to-wishlist a:not(.alt),
.product-content .compare,
.tagged_as{
    font-size: 14px;
    font-weight: 400;
    text-transform: uppercase;
}
.quantity.buttons_added .minus:hover:before, .quantity.buttons_added .plus:hover:before {
    color: #e6bf4e;
}
.minus, .plus, .minus + .input-text {
    background: transparent !important;
    border: 1px solid #d1d1d1 !important;
    color: #4d4d4d !important;
}
.plus {
    margin-left: -6px;
}
.minus {
    margin-right: -6px;
}
.product-side-information-inner .price *{
    color:#1c1a1a !important;
    font-size:50px !important;
}
.single-product-booking .product-information .title{
    font-size: 40px;
    font-weight: 400;
    text-transform: capitalize;
    margin-bottom:10px;
}
.product-side-information-inner .product-price-title{
    display:none !important;
}

.single-product .product-side-information-inner > div{
    background:transparent;
    border:1px solid #ededed;
}

/*content*/

input[type="text"]:focus,
input[type="email"]:focus,
input[type="password"]:focus,
textarea:focus{
    border-color:#e6bf4e !important;
}
.wpcf7-form-control.wpcf7-submit{
    margin-top:25px;
}
.wpcf7-submit,
.single-product .add_to_cart_button{
       background: #e6bf4e !important;
    color: #fff !important;
    letter-spacing: 1px !important;
    font-size: 14px !important;
    padding: 0px 18px !important;
    font-family: "Open Sans" !important;
    font-weight: 800;
    height: 50px !important;
     box-shadow: 5px 5px 0px 0px #fbda7b !important;
     width:164px;
     border:1px solid #e6bf4e !important;
}
.wpcf7-submit:hover,
.single-product .add_to_cart_button:hover{
    background:#1c1a1a !important;
    color:#fff !important;
    border-color:#1c1a1a !important;
}

.etheme_portfolio-template-default .page-heading{
    background-image:none !important;
}
.hover-bg:hover img,
.vc_gitem-animated-block:hover *{
    -webkit-transform: scale(1.08);
    -ms-transform: scale(1.08);
    transform: scale(1.08);
    transition: all 0.4s ease-in-out;
}
.hover-bg img,
.vc_gitem-animated-block *{
    transition: all 0.4s ease-in-out;
}
.hover-bg,
.vc_gitem-animated-block {
    position: relative;
    overflow: hidden;
    cursor: pointer;
    -webkit-transition: all 0.3s ease-in-out;
    transition: all 0.3s ease-in-out;
    backface-visibility: hidden;
    -webkit-backface-visibility: hidden;
}

.vc_gitem-zone-mini h3:hover{
    color:#e6bf4e;
}
.vc_gitem-zone-mini h3{
    font-size: 16px;
    font-family: "Open Sans";
    font-weight: 600;
    margin-top: 15px;
    margin-bottom: 10px;
}
.btn_shadow{
    letter-spacing:1px;
    box-shadow: 5px 5px 0px 0px #fbda7b !important;
}

.price-block:hover{
    border-bottom-color:#fbda7b !important;
    border-left-color:#fbda7b !important;
    border-right-color:#fbda7b !important;
}
/*footer*/
footer.footer ul li{
    margin-left:20px;
}
footer.footer .menu > li > a {
    color: #1c1a1a !important;
    line-height:0;
}

body .et-follow-buttons.buttons-size-normal a{
    color:#a4a4a4 !important;
    padding:0 10px;
}
.footer.text-color-light a:hover{
    color:#fbda7b !important;
}


@media screen and (max-width: 480px) {
    .single-product .single-product-booking .quantity {
          position: relative;
          bottom: -15px;
     }
    .img_fot-title img{
      margin-left:60px !important;
    }
    .img_fot-title-2 img{
        margin-left:20px !important;
    }
    .fixed-enabled .header-logo img {
      max-width: 100px !important;
    }
    footer.footer *{
        text-align:center !important;
    }
    footer.footer img{
        margin-bottom:30px !important;
    }
    footer.footer .menu,
    .price-section > div{
        margin-bottom:25px !important;
    }
    footer.footer ul li,
    footer.footer .et-follow-buttons{
        margin-bottom:15px !important;
    }
    .menu-element.horizontal li{
        padding: 0 15px 0 0 !important;
    }
    .icons-typography .wpb_column.vc_column_container .vc_column-inner{
        margin-left:0px !important;
        padding-left:15px !important;
    }
    .wpb_animate_when_almost_visible .mpc-button,
    .section-about-us .mpc-icon-list,
    body .price-section,
    .wpb_revslider_element{
        margin-bottom:100px !important;
    }
    .section-team .wpb_column,
    .section-team {
        margin-top:50px !important;
    }
    .section-clients .vc_column-inner{
        padding-right: 30px !important;
        padding-left: 30px !important;
    }
    .section-services .vc_column-inner{
        width:50%;
        float:left;
    }
    .product-two .vc_inner,
    .product-four .vc_inner{
        margin-right:0 !important;
    }
    .price-section div .vc_column-inner{
        margin-left:8px !important;   
    }
    .section-services .block-left_img .wpb_single_image{
        margin-bottom:80px !important;
    }
    .contact-information .wpb_column.vc_column_container{
        margin-bottom:40px !important;
    }
    .head-after{
        text-align:center !important;]
      
}
