custom_css:
::selection {background: #ff8da7;}
/*top bar*/
.top-bar {
    text-transform:uppercase;
}
.top-bar span,
.top-bar .etheme_widget_socials a i,
.top-panel-open{
    color:#aaaaaa;
}
.comment-respond input{
    border-color:#38263f !important;
}
.top-bar .etheme_widget_socials a:hover i,
.shopping-cart-widget .woocommerce-Price-amount{
  color:#ff8da7;
}
.header-color-white .navbar-header .et-wishlist-widget .wishlist-count,
.shopping-container.ico-design-4 .cart-bag .badge-number{
    background:#ff8da7 !important;
    color:#fff !important;
}
.et-header-full-width .fixed-header .container{
     -webkit-box-shadow: 0px 2px 12px 0px rgba(66,32,77,1);
-moz-box-shadow: 0px 2px 12px 0px rgba(66,32,77,1);
box-shadow: 0px 2px 12px 0px rgba(66,32,77,1);
}
/*header*/
.fixed-header{
    border:none;
}
.page-heading{
  margin-bottom:60px;
  border-top:1px solid #38263f;
  border-bottom:1px solid #38263f;
}
.header .menu-wrapper li a:after,
.fixed-header .menu-wrapper li a:after{
    display:none;
}
.header .menu-wrapper li:hover,
.fixed-header .menu-wrapper li:hover{
    opacity:.6;
}
.header .menu-wrapper li.current-menu-item a,
.fixed-header li.current-menu-item a{
    opacity:.6;
}
/*Content*/
.cart_totals{
    background:#42204d !important;
}
.single-product .carousel-area .products-title{
   display:none;
}

.mpc-lightbox{
    position: absolute;
    right: 43%;
    top: 30%;
}
.product-remove a,
.actions .btn.gray,
.coupon .btn{
    background:#ff8da7 !important;
}
.owl-buttons .owl-prev,
.owl-buttons .owl-next{
    background:transparent !important;
}
.single-product .wc-tabs{display:none;}
.owl-buttons .owl-prev:hover::before,
.owl-buttons .owl-next:hover::before{
    color:#ff8da7;
}

.form-subs .wpcf7-submit:hover{
    background:#ff83a0 !important;
}
.product-title a{
    letter-spacing:4px;
    font-weight:700 !important;
}
.product-details .price .woocommerce-Price-amount{
    color:#ff83a0;
}
body .star-rating span::before{
    color:#f4e057 !important;
}
.content-page, .page-content {
    padding-bottom:0;
}
.header-wrapper.header-double-menu .container-wrapper {
    border-bottom:none;
}
header.header .menu-main-container li a{
    letter-spacing:4px;
}
.letter-spacing-7{
    letter-spacing:7px;
}
.letter-spacing-5{
    letter-spacing:5px;
}
.letter-spacing-2{
    letter-spacing:2px;
}
.articles-pagination{
    border-color:#38263f;
}

.post-categories{
    display:none;
}
.shop-categories{
    box-shadow: 0 0 24px 7px #1c0c22;
    margin-bottom:60px;
}
.customers-block,
.testimonial-block .mpc-testimonial{
     box-shadow: 0 0 24px 7px #1c0c22;
}
.categories p{
    color:#98829f !important;
}
.categories .mpc-divider-wrap{
    display:none;
}
.mpc-icon-column__heading{
    letter-spacing:2px;
}
.blog .post-heading-inner .entry-title a{
        font-size: 30px;
    color: #ff8da7;
      letter-spacing:5px;
}

article.blog-post .wp-picture.blog-hover-animated .btn, article.post-grid .wp-picture.blog-hover-animated .btn {
    top:-39px !important;
}
.blog .meta-post > a{
    color:#fff !important;
}
.blog .content-article p,
.blog .content-article{
    color:#ac92b6;
}
.blog .read-more{
    color:#fffeff;
    text-align:center !important;
    margin:25px auto 0 auto !important;
}
.blog .read-more::before{
    background-color:#fffeff !important;
}
.blog .post-comments-count span,
.entry-date{
    color:#fffeff;
}
.pagination-cubic .page-numbers li a{
    background:transparent;
    color:#98829f;
}
.articles-pagination span.page-numbers{
    background:#98829f;
    border-color:#98829f;
    border-radius:11px;
}
.articles-pagination a.page-numbers:hover{
    background:#98829f;
    color:#fff;
    border-radius:11px;
}
.articles-pagination a.page-numbers{
    border:none;
    color:#98829f;
}
/*content*/
.vc_pagination-item.vc_active a{
    opacity:1 !important;
}
.vc_pagination-item a{
    opacity:.5 !important;
    margin:5px 4px !important;
}

.testimonial-block .mpc-testimonial{
    width:80%;
    margin:20px auto;
}
.testimonial-block .mpc-testimonial__signature span{
    letter-spacing:5px;
}

/*Shop*/
.shop-banner{
    padding:40px 20px;
    background:#42204d;
    border-right:1px solid #684d71;
}
.fancy-select .trigger{
    color:#98829f !important;
}
div.fancy-select div.trigger, .view-switcher .switch-list a, .view-switcher .switch-grid a, div.fancy-select div.trigger.open, div.fancy-select ul.options {
    background-color: #342938;
}
.fancy-select .trigger::after{
border-top-color:#98829f !important;
}
.open-filters-btn a,
.archive .products-per-page span{
    color:#fff !important;
}
.fixed-content .title,
.tagged_as{
    color:#24102c;
}
.single-product .productCarousel{
    margin-bottom:5vw;
}
.single-product .images.main-slider-on{
    padding: 29px 0px;
    background: #fff;
}
.products-title{
    margin-bottom:30px;
     margin-top:40px;
}
body .qty-span{
    color:#aaa;
}
.products-title span {
  font-size:40px;    
}
.shop-banner:last-child{
    border-right:none;
}
body.single-product .product-information a.add_to_wishlist::before{
    background:transparent !important;
}
.product-information .share-title,
.product-information .menu-social-icons a i,
.product-information .tagged_as a:hover,
.product-information .woocommerce-review-link{
    color: #98829f !important;
}
body .woocommerce-Price-amount{
    font-family:Yantramanav !important;
    letter-spacing:3px;
}
.product-share{
    border:none;
}
.buttons_added span,
.buttons_added input{
    background:transparent !important;
    border:none;
}
.product-information-inner .title{

    letter-spacing:6px;
    font-size:26px;
}
.product-information-inner .size-guide {
    background:#fff url(/demo/xstore/wp-content/themes/xstore-animals/images/size-guide.png) no-repeat 10px center!important;
}
.product-information-inner .size-guide a {
    color:#24102c;
}
.product-information-inner .buttons_added span:hover,
.product-information-inner .add_to_cart_button:hover{
    color:#fff !important;
    background:#ff8da7 !important;
    border-color:#ff8da7 !important;
}
.product-information-inner .add_to_wishlist{
    color:#ff8da7 !important;
}
.single-product .product-information-inner .woocommerce-Price-amount{
    font-size:30px;
    color:#ff8da7;
}
.single-product .product-information-inner .add_to_cart_button,
.quick-view-popup .product-information .add_to_cart_button{
    border:1px solid #ff8da7;
    background:transparent;
    color:#3e3144;
    letter-spacing:3px;
    font-weight:bold;
    font-size:12px;
    border-radius:21px;
}
.quick-view-popup .product-information .add_to_cart_button:hover{
    background:#f37e99 !important;
}
.quick-view-popup .product-information .add_to_cart_button{
    color:#fff;
    background:#ff83a0;
}
.tagged_as{
    color:#aaa;
}
.product-information .woocommerce-Price-amount{
    color:#ff83a0;
}
.product-information-inner p{
    color:#98829f;
}
.product-information-inner .fixed-content{
    padding:63px;
    background:#fff;
}
.open-filters-btn a,
.view-switcher .switcher-active a,
.view-switcher .switch-grid:hover a,
.view-switcher .switch-list:hover a,
.page-numbers .page-numbers.current,
.page-numbers a.page-numbers:hover{
    background:#42204d !important;
    border-color:#42204d !important;
    color:#98829f;
}
.ajax-loader{
    display:none;
}
.page-numbers a.page-numbers{
    color:#7f6887;
    border:none;
}
.archive .filter-wrap{
    border-top:none !important;
}
.after-shop-loop,
.woocommerce-result-count,
.filter-wrap,
.top-bar,
.languages-area .topbar-widget,
.top-links .topbar-widget,
.top-bar .et-follow-buttons a,
.view-switcher,
.products-per-page,
.wishlist-dropdown,
.wishlist-dropdown p,
.wishlist-dropdown .cart-widget-products li,
.cart_list p,
.cart-popup-container,
.cart-popup-container .cart-widget-products li,
.shop_table,
.shop_table th,
.shop_table .cart_item td,
.block-title,
.checkout_coupon input,
.cart_item td,
.woocommerce-checkout .step-title,
.woocommerce-checkout input,
.woocommerce-checkout textarea,
.woocommerce-checkout  .select2-drop,
.woocommerce table.wishlist_table tbody td,
.woocommerce table.wishlist_table thead th,
.wishlist_table td,
.woocommerce .u-column1,
.woocommerce-account .woocommerce h2,
.single-post .related-posts .title,
.single-post .form-group textarea,
.single-post .form-group input,
.single-post .comment-reply-title,
.single-post .share-post,
.single-post  .author-info{
    border-color:#38263f !important;
}
.mobile-menu-wrapper .header-search.act-default .search-btn {
    background-color: #4b3b52;
    color: #808080;
    font-weight: 400;
}
.cart_totals > h2, .cart_totals th, table .cart-subtotal td {
    border-color: rgba(36, 16, 44, 0.5) !important;
}
.mobile-sidebar-widget .et-follow-buttons a i{
    color:#ff8da7;
}
.single-post blockquote {
    border-left-color: #38263f !important;
}
.woocommerce-checkout .woocommerce-info,
.woocommerce-checkout .order-review,
.form-row .woocommerce-Button{
    background:#42204d !important;
}
.mobile-menu-wrapper .menu > li,
.mobile-menu-wrapper .links li,
.mobile-menu-wrapper .mobile-sidebar-widget.etheme_widget_socials,
.mobile-menu-wrapper .links,
.mobile-menu-wrapper .header-search.act-default{
    border-color:#4b3b52 !important;
}
.woocommerce-checkout .woocommerce-info{
    border-right-color: #38263f;
    border-bottom-color: #38263f;
    border-left-color: #38263f;
}
.shop_table{
    margin-bottom:5vw;
}
.product-add-to-cart a{
    background:#ff8da7 !important;
}
.after-shop-loop,
.articles-pagination{
    margin-bottom:5vw;
}

    
/*footer*/
footer .mpc-divider-wrap{
    display:none;
}
footer h2{
    letter-spacing:3px;
}
.footer-text p{
    font-size:13px !important;
}
footer.footer a:hover{
    color:#ff8da7 !important;
}
.footer-copyrights-right img:hover{
    opacity:1;
}
footer.footer .et-follow-buttons a:hover i{
    color:#ff83a0;
}
.navbar-collapse,
.navbar-collapse .header-search{
    background:#42204d !important;
}

@media only screen and ( min-width:1200px ){
   .blog .content article:nth-child(odd) .post-data{
    text-align:center;
    padding:64px;
    border-radius:75px;
    background:#16061c;
    position:relative;
    left:-70px;
     z-index:99;
     display: inline-table;
    width: 610px;
}
.blog .content article:nth-child(even) .post-data{
    text-align:center;
    padding:64px;
    border-radius:75px;
    background:#16061c;
    position:relative;
    right:-70px !important;
    z-index:99;
    display: inline-table;
    width: 610px;
}
}

@media only screen and (min-width: 768px) and (max-width: 1024px){
  body .wpcf7-validates-as-email {
    width: 64% !important;
   }
   body .testimonial-block .mpc-testimonial{
    padding:50px 40px !important;

  }
  .customers-block-home .wpb_text_column{
    margin-bottom:20px !important;
}

.awesome-block-head .wpb_text_column,
.video-block .wpb_text_column,
.video-block .mpc-image{
    margin-bottom:30px !important;
}
.home-banner-mr > .vc_column-inner{
    min-height:180px;
}
}
   
   
    
@media only screen and ( max-width:767px ){
.responsive-center,
.et-follow-buttons,
.blog .content article .post-data{
    text-align:center !important;
}
.margin-bottom-none{
    margin-bottom:0 !important;
}
.padding-bottom-responsive{
    padding-top:20px;
}
.respnsive-margin-bottom{
    margin-bottom:50px;
}
.after-shop-loop,
.articles-pagination{
    margin-bottom:120px;
}
body .home-banner-mr .vc_column-inner {
    margin-top: 0px !important;
}
body .last-block{
    margin-bottom:110px !important;
}
body .contact-us-block{
    padding-bottom:0 !important;
}
.mpc-lightbox{
    position: absolute;
    right: 45% !important;
    top: 32% !important;
}
.wpcf7-validates-as-email {
    width:100% !important;
     margin-bottom:20px !important;
    margin-top:20px;
}
.home .wpcf7-form-control{
    float:none;
}
.shop-banner,
.home-banner-mr{
    margin-bottom:1px !important;
}

.icon-box_section  .mpc-icon{
    float:left;
    width:8%;
    margin-top:6px !important;
}
.icon-box_section  .mpc-icon-column__content-wrap{
    width:90%;
    float:right;
    text-align:left !important;
}
.icon-box_section  .location .mpc-icon-column__content p,
.icon-box_section  .mpc-icon-column__content .mpc-icon-column__description{
    text-align:left !important;
}
.button-banner .mpc-button__content{
    background:#ff83a0;
}
footer.footer{
    padding-top:40px;
    padding-bottom:15px;
}
.none{
    display:none;
}
.null-instagram-feed .instagram-pics li {
    padding:0 !important;

}
.hover-bg .wpb_wrapper .wpb_content_element:last-child{
    margin-bottom:0 !important;
}
body .testimonial-block .mpc-testimonial{
    padding:25px 15px !important;
    margin: 20px auto 80px auto ;
}
.customers-block-home .wpb_text_column{
    margin-bottom:20px !important;
}
.mobile-menu-wrapper .menu > li a, .mobile-menu-wrapper .links li a {
    color: #fff !important;
}
.awesome-block-head .wpb_text_column,
.video-block .wpb_text_column,
.video-block .mpc-image{
    margin-bottom:30px !important;
}
.mpc-interactive_image .mpc-hotspot__icon{
    font-size:20px !important;
}
.submit-form>input {
    margin-left: 0px !important;
}
.submit-form{
    margin: 0 auto 40px;
}
body .subscribe-block{
    padding:40px 0 150px 0 !important;
}
}

@media only screen and ( max-width:481px ){
   .mpc-lightbox{
    right: 38% !important;
    top: 21% !important;
}
}
